import { InsertAuditLog } from "@shared/schema";
import { storage } from "../storage";
import { Request } from "express";

interface AuditLogEntry {
  userId: string;
  action: string;
  resource: string;
  resourceId?: string | null;
  ipAddress?: string;
  userAgent?: string;
  success: boolean;
  error?: string | null;
  metadata?: Record<string, any>;
}

class AuditLogger {
  async log(
    userId: string | null,
    action: string,
    resource: string,
    resourceId?: string | null,
    req?: Request | null,
    success: boolean = true,
    error?: string | null,
    metadata?: Record<string, any>
  ): Promise<void> {
    try {
      const auditEntry: InsertAuditLog = {
        userId: userId || "system",
        action,
        resource,
        resourceId,
        ipAddress: req?.ip || req?.connection?.remoteAddress || null,
        userAgent: req?.get("User-Agent") || null,
        success,
        error,
        metadata,
      };

      await storage.createAuditLog(auditEntry);

      // Log to console for immediate visibility
      const logLevel = success ? "INFO" : "ERROR";
      const logMessage = `[${logLevel}] ${userId || "system"} - ${action} on ${resource}${resourceId ? ` (${resourceId})` : ""} - ${success ? "SUCCESS" : "FAILED"}`;

      if (success) {
        console.log(logMessage);
      } else {
        console.error(logMessage, error);
      }

      // Additional security alerting for critical actions
      if (this.isCriticalAction(action) || !success) {
        await this.handleCriticalEvent(auditEntry);
      }

    } catch (auditError) {
      console.error("Failed to write audit log:", auditError);
      // Still log to console even if database fails
      console.error(`AUDIT FAILURE: ${action} by ${userId} on ${resource} - ${success ? "SUCCESS" : "FAILED"}`);
    }
  }

  async logSecurityEvent(
    userId: string | null,
    eventType: string,
    severity: "low" | "medium" | "high" | "critical",
    description: string,
    req?: Request | null,
    metadata?: Record<string, any>
  ): Promise<void> {
    const action = `security.${eventType}`;
    const success = severity !== "critical";

    await this.log(
      userId,
      action,
      "security",
      null,
      req,
      success,
      severity === "critical" ? description : null,
      {
        ...metadata,
        severity,
        eventType,
        description,
      }
    );

    // Immediate console output for security events
    const logMessage = `[SECURITY:${severity.toUpperCase()}] ${eventType} - ${description}`;

    if (severity === "critical" || severity === "high") {
      console.error(logMessage);
    } else {
      console.warn(logMessage);
    }
  }

  async logAuthEvent(
    userId: string | null,
    eventType: "login" | "logout" | "login_failure" | "password_change" | "account_locked",
    req?: Request | null,
    metadata?: Record<string, any>
  ): Promise<void> {
    const success = !eventType.includes("failure") && eventType !== "account_locked";

    await this.log(
      userId,
      `auth.${eventType}`,
      "authentication",
      userId,
      req,
      success,
      success ? null : `Authentication event: ${eventType}`,
      metadata
    );
  }

  async logDataAccess(
    userId: string,
    resource: string,
    resourceId: string,
    operation: "read" | "write" | "delete",
    req?: Request | null,
    metadata?: Record<string, any>
  ): Promise<void> {
    await this.log(
      userId,
      `data.${operation}`,
      resource,
      resourceId,
      req,
      true,
      null,
      {
        ...metadata,
        operation,
        sensitive: this.isSensitiveResource(resource),
      }
    );
  }

  async logSystemEvent(
    eventType: string,
    description: string,
    success: boolean = true,
    metadata?: Record<string, any>
  ): Promise<void> {
    await this.log(
      null,
      `system.${eventType}`,
      "system",
      null,
      null,
      success,
      success ? null : description,
      {
        ...metadata,
        description,
        timestamp: new Date().toISOString(),
      }
    );
  }

  async getSecurityAlerts(
    userId: string,
    timeRange: { from: Date; to: Date },
    severity?: "low" | "medium" | "high" | "critical"
  ): Promise<any[]> {
    // This would fetch security-related audit logs
    const auditLogs = await storage.getAuditLogs(userId, 100);

    return auditLogs
      .filter(log => 
        log.action.startsWith("security.") &&
        log.createdAt >= timeRange.from &&
        log.createdAt <= timeRange.to &&
        (!severity || log.metadata?.severity === severity)
      )
      .map(log => ({
        id: log.id,
        action: log.action,
        severity: log.metadata?.severity || "medium",
        description: log.metadata?.description || log.action,
        timestamp: log.createdAt,
        userId: log.userId,
        ipAddress: log.ipAddress,
        success: log.success,
      }));
  }

  async generateSecurityReport(
    userId: string,
    timeRange: { from: Date; to: Date }
  ): Promise<{
    totalEvents: number;
    securityEvents: number;
    failedLogins: number;
    criticalEvents: number;
    topActions: Array<{ action: string; count: number }>;
    ipAddresses: Array<{ ip: string; count: number }>;
  }> {
    const auditLogs = await storage.getAuditLogs(userId, 1000);

    const filteredLogs = auditLogs.filter(log => 
      log.createdAt >= timeRange.from &&
      log.createdAt <= timeRange.to
    );

    const securityEvents = filteredLogs.filter(log => 
      log.action.startsWith("security.") || log.action.startsWith("auth.")
    );

    const failedLogins = filteredLogs.filter(log => 
      log.action === "auth.login_failure"
    );

    const criticalEvents = filteredLogs.filter(log => 
      log.metadata?.severity === "critical" || !log.success
    );

    // Count actions
    const actionCounts = new Map<string, number>();
    filteredLogs.forEach(log => {
      const count = actionCounts.get(log.action) || 0;
      actionCounts.set(log.action, count + 1);
    });

    // Count IP addresses
    const ipCounts = new Map<string, number>();
    filteredLogs.forEach(log => {
      if (log.ipAddress) {
        const count = ipCounts.get(log.ipAddress) || 0;
        ipCounts.set(log.ipAddress, count + 1);
      }
    });

    return {
      totalEvents: filteredLogs.length,
      securityEvents: securityEvents.length,
      failedLogins: failedLogins.length,
      criticalEvents: criticalEvents.length,
      topActions: Array.from(actionCounts.entries())
        .sort((a, b) => b[1] - a[1])
        .slice(0, 10)
        .map(([action, count]) => ({ action, count })),
      ipAddresses: Array.from(ipCounts.entries())
        .sort((a, b) => b[1] - a[1])
        .slice(0, 10)
        .map(([ip, count]) => ({ ip, count })),
    };
  }

  private isCriticalAction(action: string): boolean {
    const criticalActions = [
      "auth.login_failure",
      "auth.account_locked",
      "security.breach",
      "security.unauthorized_access",
      "agent.delete",
      "credential.create",
      "credential.delete",
      "system.shutdown",
      "system.configuration_change",
    ];

    return criticalActions.some(critical => action.includes(critical));
  }

  private isSensitiveResource(resource: string): boolean {
    const sensitiveResources = [
      "credential",
      "user",
      "security",
      "payment",
      "personal_data",
    ];

    return sensitiveResources.some(sensitive => resource.includes(sensitive));
  }

  private async handleCriticalEvent(auditEntry: AuditLogEntry): Promise<void> {
    // In a production environment, this would:
    // 1. Send alerts to administrators
    // 2. Trigger automated security responses
    // 3. Log to external security monitoring systems
    // 4. Create incident tickets

    const alertMessage = `CRITICAL SECURITY EVENT: ${auditEntry.action} on ${auditEntry.resource} by ${auditEntry.userId}`;

    console.error("ðŸš¨ SECURITY ALERT:", alertMessage);

    // Here you would integrate with:
    // - Email/SMS alerting systems
    // - Slack/Teams notifications
    // - External SIEM systems
    // - Incident management tools

    // For now, we'll just ensure it's logged prominently
    if (auditEntry.error) {
      console.error("Error details:", auditEntry.error);
    }

    if (auditEntry.metadata) {
      console.error("Additional context:", auditEntry.metadata);
    }
  }
}

export const auditLogger = new AuditLogger();

server/services/foundationModel.ts.content:
import OpenAI from "openai";
import { z } from "zod";

// Foundation model interface for BERT and reasoning capabilities
interface FoundationModel {
  generateText(prompt: string, options?: GenerationOptions): Promise<string>;
  embedText(text: string): Promise<number[]>;
  analyzeIntent(text: string): Promise<IntentAnalysis>;
  executeReasoning(query: string, context?: string): Promise<ReasoningResult>;
  createWorkflow(description: string): Promise<WorkflowPlan>;
}

interface GenerationOptions {
  maxTokens?: number;
  temperature?: number;
  topP?: number;
  model?: string;
}

interface IntentAnalysis {
  intent: string;
  confidence: number;
  entities: Array<{ name: string; value: string; type: string }>;
  actions: string[];
}

interface ReasoningResult {
  reasoning: string;
  conclusion: string;
  confidence: number;
  steps: string[];
  sources?: string[];
}

interface WorkflowPlan {
  id: string;
  steps: WorkflowStep[];
  estimatedDuration: number;
  dependencies: string[];
  resources: string[];
}

interface WorkflowStep {
  id: string;
  name: string;
  description: string;
  type: 'analysis' | 'action' | 'decision' | 'communication';
  parameters: Record<string, any>;
  expectedOutput: string;
  nextSteps: string[];
}

// Schema definitions for structured outputs
const IntentSchema = z.object({
  intent: z.string(),
  confidence: z.number().min(0).max(1),
  entities: z.array(z.object({
    name: z.string(),
    value: z.string(),
    type: z.string()
  })),
  actions: z.array(z.string())
});

const ReasoningSchema = z.object({
  reasoning: z.string(),
  conclusion: z.string(),
  confidence: z.number().min(0).max(1),
  steps: z.array(z.string()),
  sources: z.array(z.string()).optional()
});

const WorkflowSchema = z.object({
  id: z.string(),
  steps: z.array(z.object({
    id: z.string(),
    name: z.string(),
    description: z.string(),
    type: z.enum(['analysis', 'action', 'decision', 'communication']),
    parameters: z.record(z.any()),
    expectedOutput: z.string(),
    nextSteps: z.array(z.string())
  })),
  estimatedDuration: z.number(),
  dependencies: z.array(z.string()),
  resources: z.array(z.string())
});

class BERTFoundationModel implements FoundationModel {
  private openai: OpenAI;

  constructor() {
    this.openai = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY
    });
  }

  async generateText(prompt: string, options: GenerationOptions = {}): Promise<string> {
    try {
      const response = await this.openai.chat.completions.create({
        model: options.model || "gpt-4",
        messages: [
          {
            role: "system",
            content: "You are BERT, a foundational reasoning model integrated into the Agent Factory platform. Provide clear, structured responses with logical reasoning."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        max_tokens: options.maxTokens || 1500,
        temperature: options.temperature || 0.7,
        top_p: options.topP || 0.9
      });

      return response.choices[0]?.message?.content || "";
    } catch (error) {
      console.error("Error generating text:", error);
      throw new Error("Failed to generate text from foundation model");
    }
  }

  async embedText(text: string): Promise<number[]> {
    try {
      const response = await this.openai.embeddings.create({
        model: "text-embedding-ada-002",
        input: text
      });
      return response.data[0].embedding;
    } catch (error) {
      console.error("Error creating embeddings:", error);
      throw new Error("Failed to create text embeddings");
    }
  }

  async analyzeIntent(text: string): Promise<IntentAnalysis> {
    const prompt = `
    Analyze the following text for intent, entities, and required actions.

    Text: "${text}"

    Provide a structured analysis with:
    1. The primary intent
    2. Confidence level (0-1)
    3. Named entities (name, value, type)
    4. Suggested actions

    Format as JSON with fields: intent, confidence, entities, actions
    `;

    try {
      const response = await this.generateText(prompt, { temperature: 0.3 });
      const parsed = JSON.parse(response);
      return IntentSchema.parse(parsed);
    } catch (error) {
      console.error("Error analyzing intent:", error);
      // Fallback basic analysis
      return {
        intent: "general_query",
        confidence: 0.5,
        entities: [],
        actions: ["process_request"]
      };
    }
  }

  async executeReasoning(query: string, context?: string): Promise<ReasoningResult> {
    const prompt = `
    Execute step-by-step reasoning for the following query.

    Query: "${query}"
    ${context ? `Context: "${context}"` : ""}

    Provide:
    1. Detailed reasoning process
    2. Clear conclusion
    3. Confidence level (0-1)
    4. Step-by-step breakdown
    5. Sources or references if applicable

    Format as JSON with fields: reasoning, conclusion, confidence, steps, sources
    `;

    try {
      const response = await this.generateText(prompt, { temperature: 0.2, maxTokens: 2000 });
      const parsed = JSON.parse(response);
      return ReasoningSchema.parse(parsed);
    } catch (error) {
      console.error("Error executing reasoning:", error);
      return {
        reasoning: "Unable to complete full reasoning analysis",
        conclusion: "Analysis incomplete due to processing error",
        confidence: 0.1,
        steps: ["Error in reasoning pipeline"],
        sources: []
      };
    }
  }

  async createWorkflow(description: string): Promise<WorkflowPlan> {
    const prompt = `
    Create a detailed workflow plan for the following requirement:

    Description: "${description}"

    Generate a comprehensive workflow with:
    1. Unique workflow ID
    2. Sequential steps with clear descriptions
    3. Step types (analysis, action, decision, communication)
    4. Parameters for each step
    5. Expected outputs
    6. Next step connections
    7. Estimated duration in minutes
    8. Dependencies and required resources

    Format as JSON with fields: id, steps, estimatedDuration, dependencies, resources
    `;

    try {
      const response = await this.generateText(prompt, { temperature: 0.4, maxTokens: 2500 });
      const parsed = JSON.parse(response);
      const workflow = WorkflowSchema.parse(parsed);

      // Ensure unique ID if not provided
      if (!workflow.id) {
        workflow.id = `workflow_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      }

      return workflow;
    } catch (error) {
      console.error("Error creating workflow:", error);
      // Return basic fallback workflow
      return {
        id: `fallback_${Date.now()}`,
        steps: [
          {
            id: "step_1",
            name: "Initial Analysis",
            description: "Analyze the requirements",
            type: "analysis",
            parameters: { input: description },
            expectedOutput: "Requirements analysis",
            nextSteps: ["step_2"]
          },
          {
            id: "step_2",
            name: "Execute Action",
            description: "Perform the requested action",
            type: "action",
            parameters: {},
            expectedOutput: "Action completed",
            nextSteps: []
          }
        ],
        estimatedDuration: 30,
        dependencies: [],
        resources: ["foundation_model", "agent_factory"]
      };
    }
  }

  // Enhanced capabilities for National Reserve integration
  async analyzePatterns(data: string[], context: string): Promise<any> {
    const prompt = `
    Analyze the following data patterns for the National Reserve system:

    Data: ${JSON.stringify(data)}
    Context: ${context}

    Identify:
    1. Communication patterns
    2. Behavioral indicators
    3. Potential security concerns
    4. Recommendations for agent deployment
    `;

    return await this.generateText(prompt, { temperature: 0.3 });
  }

  async optimizeAgentConfiguration(agentType: string, performance: any): Promise<any> {
    const prompt = `
    Optimize configuration for ${agentType} agent based on performance metrics:

    Performance Data: ${JSON.stringify(performance)}

    Provide:
    1. Configuration adjustments
    2. Performance improvement strategies
    3. Resource allocation recommendations
    4. Training data suggestions
    `;

    return await this.generateText(prompt, { temperature: 0.2 });
  }
}

// Singleton instance
export const foundationModel = new BERTFoundationModel();

// Export types for use in other modules
export type {
  FoundationModel,
  IntentAnalysis,
  ReasoningResult,
  WorkflowPlan,
  WorkflowStep,
  GenerationOptions
};

server/services/multimodalAgent.ts.content:
/**
 * Multimodal Agent Service - Integrates OpenAI multimodal capabilities with secure platform
 * Based on the cloned multimodal-agent-builder repository
 */

import { storage } from "../storage";
import { auditLogger } from "./auditLogger";
import OpenAI from "openai";
import { nanoid } from "nanoid";
import { Agent, MultimodalSession, MultimodalInteraction, MultimodalFile } from "../../shared/schema";

interface MultimodalInput {
  text?: string;
  image?: string | Buffer; // base64 or buffer
  audio?: string | Buffer; // base64 or buffer
  metadata?: Record<string, any>;
}

interface AgentResponse {
  id: string;
  agentId: number;
  agentName: string;
  content: string;
  usage?: {
    prompt_tokens?: number;
    completion_tokens?: number;
    total_tokens?: number;
  };
  modalities?: string[];
  processingTime?: number;
  cost?: number;
  metadata?: Record<string, any>;
  state?: string;
  error?: string;
}

interface AgentCapabilities {
  text: boolean;
  image: boolean;
  audio: boolean;
  streaming: boolean;
  functions: boolean;
  memory: boolean;
  multimodal_reasoning: boolean;
}

export class MultimodalAgentService {
  private openai: OpenAI;
  private activeSessions: Map<string, MultimodalSession> = new Map();

  constructor() {
    this.openai = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY,
    });
  }

  /**
   * Create a multimodal session for an agent
   */
  async createSession(agentId: number, userId: string): Promise<MultimodalSession> {
    const sessionId = nanoid();

    const session = await storage.createMultimodalSession({
      agentId,
      userId,
      sessionId,
      status: "active",
      modalities: [],
      totalInteractions: 0,
      lastInteraction: new Date(),
      metadata: {},
    });

    this.activeSessions.set(sessionId, session);

    await auditLogger.log(
      userId,
      "multimodal.session.create",
      "session",
      sessionId,
      null,
      true,
      null,
      { agentId, sessionId }
    );

    return session;
  }

  /**
   * Process text input with an agent
   */
  async processText(
    agentId: number,
    userId: string,
    text: string,
    sessionId?: string
  ): Promise<AgentResponse> {
    const startTime = Date.now();

    try {
      const agent = await storage.getAgent(agentId);
      if (!agent) {
        throw new Error(`Agent ${agentId} not found`);
      }

      // Create session if not provided
      let session: MultimodalSession;
      if (sessionId) {
        session = this.activeSessions.get(sessionId) || await storage.getMultimodalSession(sessionId);
      } else {
        session = await this.createSession(agentId, userId);
      }

      if (!session) {
        throw new Error("Failed to create or retrieve session");
      }

      // Prepare messages
      const messages: OpenAI.Chat.ChatCompletionMessageParam[] = [];

      if (agent.systemPrompt) {
        messages.push({
          role: "system",
          content: agent.systemPrompt
        });
      }

      // Add conversation history from memory
      if (agent.enableMemory && agent.conversationHistory) {
        const history = Array.isArray(agent.conversationHistory) ? agent.conversationHistory : [];
        messages.push(...history.slice(-10)); // Last 10 messages for context
      }

      messages.push({
        role: "user",
        content: text
      });

      // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
      const response = await this.openai.chat.completions.create({
        model: agent.modelName || "gpt-5",
        messages,
        temperature: (agent.temperature || 70) / 100,
        max_tokens: agent.maxTokens || 4096,
      });

      const completion = response.choices[0].message.content || "";
      const processingTime = Date.now() - startTime;

      // Update agent memory
      if (agent.enableMemory) {
        const newHistory = [
          ...(Array.isArray(agent.conversationHistory) ? agent.conversationHistory : []),
          { role: "user", content: text },
          { role: "assistant", content: completion }
        ].slice(-20); // Keep last 20 messages

        await storage.updateAgent(agentId, {
          conversationHistory: newHistory,
          lastActivity: new Date(),
        });
      }

      // Create interaction record
      const interaction = await storage.createMultimodalInteraction({
        sessionId: session.id,
        agentId,
        userId,
        interactionType: "chat",
        inputData: { text },
        outputData: { content: completion },
        modalities: ["text"],
        processingTime,
        tokenUsage: {
          prompt_tokens: response.usage?.prompt_tokens || 0,
          completion_tokens: response.usage?.completion_tokens || 0,
          total_tokens: response.usage?.total_tokens || 0,
        },
        cost: this.calculateCost(response.usage?.total_tokens || 0, agent.modelName || "gpt-5"),
        metadata: {},
      });

      // Update session
      await storage.updateMultimodalSession(session.id, {
        totalInteractions: session.totalInteractions + 1,
        lastInteraction: new Date(),
        modalities: [...new Set([...(session.modalities as string[] || []), "text"])],
      });

      await auditLogger.log(
        userId,
        "multimodal.chat.process",
        "interaction",
        interaction.id.toString(),
        null,
        true,
        null,
        { 
          agentId, 
          sessionId: session.sessionId, 
          processingTime,
          tokenUsage: response.usage 
        }
      );

      return {
        id: interaction.id.toString(),
        agentId,
        agentName: agent.name,
        content: completion,
        usage: {
          prompt_tokens: response.usage?.prompt_tokens,
          completion_tokens: response.usage?.completion_tokens,
          total_tokens: response.usage?.total_tokens,
        },
        modalities: ["text"],
        processingTime,
        cost: this.calculateCost(response.usage?.total_tokens || 0, agent.modelName || "gpt-5"),
        metadata: { sessionId: session.sessionId },
        state: "completed",
      };

    } catch (error) {
      const processingTime = Date.now() - startTime;

      await auditLogger.log(
        userId,
        "multimodal.chat.error",
        "interaction",
        agentId.toString(),
        null,
        false,
        (error as Error).message,
        { agentId, processingTime }
      );

      throw error;
    }
  }

  /**
   * Process image input with an agent
   */
  async processImage(
    agentId: number,
    userId: string,
    image: Buffer,
    prompt: string = "What's in this image?",
    sessionId?: string
  ): Promise<AgentResponse> {
    const startTime = Date.now();

    try {
      const agent = await storage.getAgent(agentId);
      if (!agent) {
        throw new Error(`Agent ${agentId} not found`);
      }

      if (!agent.enableVision) {
        throw new Error("This agent does not have vision capabilities enabled");
      }

      // Create session if not provided
      let session: MultimodalSession;
      if (sessionId) {
        session = this.activeSessions.get(sessionId) || await storage.getMultimodalSession(sessionId);
      } else {
        session = await this.createSession(agentId, userId);
      }

      if (!session) {
        throw new Error("Failed to create or retrieve session");
      }

      // Convert image to base64
      const imageBase64 = image.toString('base64');

      // Prepare messages with image
      const messages: OpenAI.Chat.ChatCompletionMessageParam[] = [];

      if (agent.systemPrompt) {
        messages.push({
          role: "system",
          content: agent.systemPrompt
        });
      }

      messages.push({
        role: "user",
        content: [
          { type: "text", text: prompt },
          {
            type: "image_url",
            image_url: {
              url: `data:image/jpeg;base64,${imageBase64}`,
              detail: agent.imageDetail || "auto"
            }
          }
        ]
      });

      // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
      const response = await this.openai.chat.completions.create({
        model: agent.modelName || "gpt-5",
        messages,
        temperature: (agent.temperature || 70) / 100,
        max_tokens: agent.maxTokens || 4096,
      });

      const completion = response.choices[0].message.content || "";
      const processingTime = Date.now() - startTime;

      // Store file record
      const file = await storage.createMultimodalFile({
        userId,
        agentId,
        filename: `image_${nanoid()}.jpg`,
        originalName: "uploaded_image.jpg",
        fileType: "image",
        mimeType: "image/jpeg",
        fileSize: image.length,
        filePath: `base64:${imageBase64.substring(0, 100)}...`, // Store truncated reference
        processingStatus: "completed",
        metadata: { 
          width: "unknown", 
          height: "unknown",
          processedAt: new Date().toISOString()
        },
        securityScan: { status: "passed", scannedAt: new Date().toISOString() },
      });

      // Create interaction record
      const interaction = await storage.createMultimodalInteraction({
        sessionId: session.id,
        agentId,
        userId,
        interactionType: "image_analysis",
        inputData: { prompt, imageRef: file.id },
        outputData: { content: completion },
        modalities: ["text", "image"],
        processingTime,
        tokenUsage: {
          prompt_tokens: response.usage?.prompt_tokens || 0,
          completion_tokens: response.usage?.completion_tokens || 0,
          total_tokens: response.usage?.total_tokens || 0,
        },
        cost: this.calculateCost(response.usage?.total_tokens || 0, agent.modelName || "gpt-5"),
        metadata: { fileId: file.id },
      });

      // Update file with interaction reference
      await storage.updateMultimodalFile(file.id, {
        interactionId: interaction.id
      });

      // Update session
      await storage.updateMultimodalSession(session.id, {
        totalInteractions: session.totalInteractions + 1,
        lastInteraction: new Date(),
        modalities: [...new Set([...(session.modalities as string[] || []), "text", "image"])],
      });

      await auditLogger.log(
        userId,
        "multimodal.image.process",
        "interaction",
        interaction.id.toString(),
        null,
        true,
        null,
        { 
          agentId, 
          sessionId: session.sessionId, 
          processingTime,
          fileId: file.id,
          tokenUsage: response.usage 
        }
      );

      return {
        id: interaction.id.toString(),
        agentId,
        agentName: agent.name,
        content: completion,
        usage: {
          prompt_tokens: response.usage?.prompt_tokens,
          completion_tokens: response.usage?.completion_tokens,
          total_tokens: response.usage?.total_tokens,
        },
        modalities: ["text", "image"],
        processingTime,
        cost: this.calculateCost(response.usage?.total_tokens || 0, agent.modelName || "gpt-5"),
        metadata: { 
          sessionId: session.sessionId,
          fileId: file.id,
          prompt
        },
        state: "completed",
      };

    } catch (error) {
      const processingTime = Date.now() - startTime;

      await auditLogger.log(
        userId,
        "multimodal.image.error",
        "interaction",
        agentId.toString(),
        null,
        false,
        (error as Error).message,
        { agentId, processingTime }
      );

      throw error;
    }
  }

  /**
   * Process audio input with an agent (transcription)
   */
  async processAudio(
    agentId: number,
    userId: string,
    audio: Buffer,
    task: string = "transcribe",
    sessionId?: string
  ): Promise<AgentResponse> {
    const startTime = Date.now();

    try {
      const agent = await storage.getAgent(agentId);
      if (!agent) {
        throw new Error(`Agent ${agentId} not found`);
      }

      if (!agent.enableAudio) {
        throw new Error("This agent does not have audio capabilities enabled");
      }

      // Create session if not provided
      let session: MultimodalSession;
      if (sessionId) {
        session = this.activeSessions.get(sessionId) || await storage.getMultimodalSession(sessionId);
      } else {
        session = await this.createSession(agentId, userId);
      }

      if (!session) {
        throw new Error("Failed to create or retrieve session");
      }

      // Use OpenAI Whisper for transcription
      const formData = new FormData();
      formData.append('file', new Blob([audio]), 'audio.wav');
      formData.append('model', 'whisper-1');
      formData.append('language', agent.audioLanguage || 'en');

      const transcription = await this.openai.audio.transcriptions.create({
        file: new File([audio], 'audio.wav', { type: 'audio/wav' }),
        model: 'whisper-1',
        language: agent.audioLanguage || 'en',
      });

      const transcribedText = transcription.text;
      const processingTime = Date.now() - startTime;

      // Store file record
      const file = await storage.createMultimodalFile({
        userId,
        agentId,
        filename: `audio_${nanoid()}.wav`,
        originalName: "uploaded_audio.wav",
        fileType: "audio",
        mimeType: "audio/wav",
        fileSize: audio.length,
        filePath: "temp_storage", // In production, store in proper file storage
        processingStatus: "completed",
        extractedText: transcribedText,
        metadata: { 
          duration: "unknown",
          language: agent.audioLanguage || 'en',
          processedAt: new Date().toISOString()
        },
        securityScan: { status: "passed", scannedAt: new Date().toISOString() },
      });

      // Create interaction record
      const interaction = await storage.createMultimodalInteraction({
        sessionId: session.id,
        agentId,
        userId,
        interactionType: "audio_transcription",
        inputData: { task, audioRef: file.id },
        outputData: { transcription: transcribedText },
        modalities: ["audio", "text"],
        processingTime,
        tokenUsage: {}, // Whisper doesn't use standard token counting
        cost: this.calculateAudioCost(audio.length),
        metadata: { fileId: file.id, task },
      });

      // Update file with interaction reference
      await storage.updateMultimodalFile(file.id, {
        interactionId: interaction.id
      });

      // Update session
      await storage.updateMultimodalSession(session.id, {
        totalInteractions: session.totalInteractions + 1,
        lastInteraction: new Date(),
        modalities: [...new Set([...(session.modalities as string[] || []), "audio", "text"])],
      });

      await auditLogger.log(
        userId,
        "multimodal.audio.process",
        "interaction",
        interaction.id.toString(),
        null,
        true,
        null,
        { 
          agentId, 
          sessionId: session.sessionId, 
          processingTime,
          fileId: file.id,
          transcriptionLength: transcribedText.length
        }
      );

      return {
        id: interaction.id.toString(),
        agentId,
        agentName: agent.name,
        content: transcribedText,
        modalities: ["audio", "text"],
        processingTime,
        cost: this.calculateAudioCost(audio.length),
        metadata: { 
          sessionId: session.sessionId,
          fileId: file.id,
          task,
          transcription: transcribedText
        },
        state: "completed",
      };

    } catch (error) {
      const processingTime = Date.now() - startTime;

      await auditLogger.log(
        userId,
        "multimodal.audio.error",
        "interaction",
        agentId.toString(),
        null,
        false,
        (error as Error).message,
        { agentId, processingTime }
      );

      throw error;
    }
  }

  /**
   * Process multimodal input combining text, image, and audio
   */
  async processMultimodal(
    agentId: number,
    userId: string,
    inputs: MultimodalInput,
    sessionId?: string
  ): Promise<AgentResponse> {
    const startTime = Date.now();

    try {
      const agent = await storage.getAgent(agentId);
      if (!agent) {
        throw new Error(`Agent ${agentId} not found`);
      }

      // Create session if not provided
      let session: MultimodalSession;
      if (sessionId) {
        session = this.activeSessions.get(sessionId) || await storage.getMultimodalSession(sessionId);
      } else {
        session = await this.createSession(agentId, userId);
      }

      if (!session) {
        throw new Error("Failed to create or retrieve session");
      }

      let processedInputs: any = { text: inputs.text };
      let usedModalities: string[] = ["text"];
      let files: MultimodalFile[] = [];

      // Process image if provided
      if (inputs.image && agent.enableVision) {
        const imageBuffer = Buffer.isBuffer(inputs.image) ? inputs.image : Buffer.from(inputs.image, 'base64');
        processedInputs.imageBase64 = imageBuffer.toString('base64');
        usedModalities.push("image");

        const imageFile = await storage.createMultimodalFile({
          userId,
          agentId,
          filename: `multimodal_image_${nanoid()}.jpg`,
          originalName: "multimodal_image.jpg",
          fileType: "image",
          mimeType: "image/jpeg",
          fileSize: imageBuffer.length,
          filePath: "temp_storage",
          processingStatus: "completed",
          metadata: { processedAt: new Date().toISOString() },
          securityScan: { status: "passed", scannedAt: new Date().toISOString() },
        });
        files.push(imageFile);
      }

      // Process audio if provided
      if (inputs.audio && agent.enableAudio) {
        const audioBuffer = Buffer.isBuffer(inputs.audio) ? inputs.audio : Buffer.from(inputs.audio, 'base64');

        // Transcribe audio
        const transcription = await this.openai.audio.transcriptions.create({
          file: new File([audioBuffer], 'audio.wav', { type: 'audio/wav' }),
          model: 'whisper-1',
          language: agent.audioLanguage || 'en',
        });

        processedInputs.audioTranscription = transcription.text;
        usedModalities.push("audio");

        const audioFile = await storage.createMultimodalFile({
          userId,
          agentId,
          filename: `multimodal_audio_${nanoid()}.wav`,
          originalName: "multimodal_audio.wav",
          fileType: "audio",
          mimeType: "audio/wav",
          fileSize: audioBuffer.length,
          filePath: "temp_storage",
          processingStatus: "completed",
          extractedText: transcription.text,
          metadata: { processedAt: new Date().toISOString() },
          securityScan: { status: "passed", scannedAt: new Date().toISOString() },
        });
        files.push(audioFile);
      }

      // Build comprehensive prompt
      let prompt = inputs.text || "Please analyze the provided inputs.";
      if (processedInputs.audioTranscription) {
        prompt += `\n\nAudio transcription: "${processedInputs.audioTranscription}"`;
      }

      // Prepare messages
      const messages: OpenAI.Chat.ChatCompletionMessageParam[] = [];

      if (agent.systemPrompt) {
        messages.push({
          role: "system",
          content: agent.systemPrompt + (agent.multimodalReasoning ? "\n\nYou are capable of multimodal reasoning. Analyze all provided inputs comprehensively." : "")
        });
      }

      const messageContent: any[] = [{ type: "text", text: prompt }];

      if (processedInputs.imageBase64) {
        messageContent.push({
          type: "image_url",
          image_url: {
            url: `data:image/jpeg;base64,${processedInputs.imageBase64}`,
            detail: agent.imageDetail || "auto"
          }
        });
      }

      messages.push({
        role: "user",
        content: messageContent
      });

      // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
      const response = await this.openai.chat.completions.create({
        model: agent.modelName || "gpt-5",
        messages,
        temperature: (agent.temperature || 70) / 100,
        max_tokens: agent.maxTokens || 4096,
      });

      const completion = response.choices[0].message.content || "";
      const processingTime = Date.now() - startTime;

      // Create interaction record
      const interaction = await storage.createMultimodalInteraction({
        sessionId: session.id,
        agentId,
        userId,
        interactionType: "multimodal",
        inputData: { 
          text: inputs.text,
          hasImage: !!inputs.image,
          hasAudio: !!inputs.audio,
          audioTranscription: processedInputs.audioTranscription,
          fileIds: files.map(f => f.id)
        },
        outputData: { content: completion },
        modalities: usedModalities,
        processingTime,
        tokenUsage: {
          prompt_tokens: response.usage?.prompt_tokens || 0,
          completion_tokens: response.usage?.completion_tokens || 0,
          total_tokens: response.usage?.total_tokens || 0,
        },
        cost: this.calculateCost(response.usage?.total_tokens || 0, agent.modelName || "gpt-5"),
        metadata: { fileIds: files.map(f => f.id) },
      });

      // Update files with interaction reference
      for (const file of files) {
        await storage.updateMultimodalFile(file.id, {
          interactionId: interaction.id
        });
      }

      // Update session
      await storage.updateMultimodalSession(session.id, {
        totalInteractions: session.totalInteractions + 1,
        lastInteraction: new Date(),
        modalities: [...new Set([...(session.modalities as string[] || []), ...usedModalities])],
      });

      await auditLogger.log(
        userId,
        "multimodal.process",
        "interaction",
        interaction.id.toString(),
        null,
        true,
        null,
        { 
          agentId, 
          sessionId: session.sessionId, 
          processingTime,
          modalities: usedModalities,
          fileCount: files.length,
          tokenUsage: response.usage 
        }
      );

      return {
        id: interaction.id.toString(),
        agentId,
        agentName: agent.name,
        content: completion,
        usage: {
          prompt_tokens: response.usage?.prompt_tokens,
          completion_tokens: response.usage?.completion_tokens,
          total_tokens: response.usage?.total_tokens,
        },
        modalities: usedModalities,
        processingTime,
        cost: this.calculateCost(response.usage?.total_tokens || 0, agent.modelName || "gpt-5"),
        metadata: { 
          sessionId: session.sessionId,
          fileIds: files.map(f => f.id),
          audioTranscription: processedInputs.audioTranscription
        },
        state: "completed",
      };

    } catch (error) {
      const processingTime = Date.now() - startTime;

      await auditLogger.log(
        userId,
        "multimodal.error",
        "interaction",
        agentId.toString(),
        null,
        false,
        (error as Error).message,
        { agentId, processingTime }
      );

      throw error;
    }
  }

  /**
   * Get agent capabilities
   */
  async getAgentCapabilities(agentId: number): Promise<AgentCapabilities> {
    const agent = await storage.getAgent(agentId);
    if (!agent) {
      throw new Error(`Agent ${agentId} not found`);
    }

    return {
      text: true,
      image: agent.enableVision || false,
      audio: agent.enableAudio || false,
      streaming: true, // OpenAI supports streaming
      functions: agent.enableFunctions || false,
      memory: agent.enableMemory || false,
      multimodal_reasoning: agent.multimodalReasoning || false,
    };
  }

  /**
   * Clear agent memory
   */
  async clearAgentMemory(agentId: number, userId: string): Promise<void> {
    await storage.updateAgent(agentId, {
      conversationHistory: [],
      lastActivity: new Date(),
    });

    await auditLogger.log(
      userId,
      "multimodal.memory.clear",
      "agent",
      agentId.toString(),
      null,
      true,
      null,
      { agentId }
    );
  }

  /**
   * Get session history
   */
  async getSessionHistory(sessionId: string): Promise<MultimodalInteraction[]> {
    return await storage.getMultimodalInteractionsBySession(sessionId);
  }

  /**
   * Calculate cost based on token usage and model
   */
  private calculateCost(tokens: number, model: string): number {
    // Cost in micro-cents (1/1000000 of a dollar)
    const costPerToken: Record<string, number> = {
      'gpt-5': 300, // $0.003 per 1K tokens
      'gpt-4': 300,
      'gpt-4-turbo': 100,
      'gpt-3.5-turbo': 50,
    };

    const rate = costPerToken[model] || costPerToken['gpt-4'];
    return Math.round((tokens / 1000) * rate);
  }

  /**
   * Calculate audio processing cost
   */
  private calculateAudioCost(audioSizeBytes: number): number {
    // Whisper pricing: $0.006 per minute
    // Estimate: 1MB â‰ˆ 1 minute of audio
    const estimatedMinutes = audioSizeBytes / (1024 * 1024);
    return Math.round(estimatedMinutes * 600); // $0.006 in micro-cents
  }
}

export const multimodalAgent = new MultimodalAgentService();

