import { storage } from "../storage";
import { agentFactory } from "./agentFactory";
import { openaiService } from "./openai";
import { auditLogger } from "./auditLogger";
import { foundationModel } from "./foundationModel";
import { Agent, InsertAgent } from "@shared/schema";

interface MilitaryRank {
  name: string;
  commandLevel: number;
  specializations: string[];
  reportingStructure: string[];
}

interface CommunicationPattern {
  type: string;
  pattern: string;
  confidence: number;
  context: string;
  translation: string;
}

interface PatternAnalysis {
  leetSpeak: boolean;
  subliminalIndicators: string[];
  communicationStyle: string;
  emotionalTone: string;
  hiddenMeaning?: string;
}

class NationalReserveService {
  private militaryHierarchy: Record<string, MilitaryRank> = {
    "five_star_general": {
      name: "Five Star General",
      commandLevel: 10,
      specializations: ["strategic_oversight", "cross_domain_intelligence", "system_optimization"],
      reportingStructure: []
    },
    "general": {
      name: "General",
      commandLevel: 9,
      specializations: ["multi_domain_oversight", "pattern_synthesis", "command_coordination"],
      reportingStructure: ["five_star_general"]
    },
    "colonel": {
      name: "Colonel",
      commandLevel: 8,
      specializations: ["operational_command", "domain_expertise", "tactical_coordination"],
      reportingStructure: ["general"]
    },
    "major": {
      name: "Major",
      commandLevel: 7,
      specializations: ["tactical_leadership", "pattern_recognition", "team_coordination"],
      reportingStructure: ["colonel"]
    },
    "captain": {
      name: "Captain",
      commandLevel: 6,
      specializations: ["unit_command", "communication_analysis", "specialized_skills"],
      reportingStructure: ["major"]
    },
    "lieutenant": {
      name: "Lieutenant",
      commandLevel: 5,
      specializations: ["junior_leadership", "real_time_monitoring", "focused_expertise"],
      reportingStructure: ["captain"]
    },
    "sergeant": {
      name: "Sergeant",
      commandLevel: 4,
      specializations: ["squad_leadership", "pattern_detection", "operational_execution"],
      reportingStructure: ["lieutenant"]
    },
    "corporal": {
      name: "Corporal",
      commandLevel: 3,
      specializations: ["team_coordination", "communication_monitoring", "task_execution"],
      reportingStructure: ["sergeant"]
    },
    "private_first_class": {
      name: "Private First Class",
      commandLevel: 2,
      specializations: ["specialized_execution", "communication_analysis", "reporting"],
      reportingStructure: ["corporal"]
    },
    "private": {
      name: "Private",
      commandLevel: 1,
      specializations: ["basic_execution", "pattern_recognition", "surveillance"],
      reportingStructure: ["private_first_class"]
    }
  };

  async deployNationalReserve(userId: string): Promise<Agent[]> {
    const deployedAgents: Agent[] = [];

    // Deploy command structure
    for (const [rankKey, rankData] of Object.entries(this.militaryHierarchy)) {
      const agentTypes = await storage.getAgentTypes();
      const agentType = agentTypes.find(type => 
        type.name.toLowerCase().replace(/\s+/g, '_') === rankKey
      );

      if (agentType) {
        const agent = await this.createMilitaryAgent(userId, agentType.id, rankData);
        deployedAgents.push(agent);
      }
    }

    // Deploy specialized intelligence units
    await this.deployIntelligenceUnits(userId, deployedAgents);

    // Initialize cross-collaboration network
    await this.initializeCollaborationNetwork(deployedAgents);

    // Activate pattern recognition systems
    await this.activatePatternRecognition(deployedAgents);

    await auditLogger.log(
      userId,
      "national_reserve.deploy",
      "agent_system",
      null,
      { deployedCount: deployedAgents.length }
    );

    return deployedAgents;
  }

  private async createMilitaryAgent(
    userId: string,
    typeId: number,
    rankData: MilitaryRank
  ): Promise<Agent> {
    const agentData: InsertAgent = {
      name: `${rankData.name} Alpha-${Math.random().toString(36).substr(2, 6).toUpperCase()}`,
      description: `Military-grade agent with ${rankData.name} authority and specialized capabilities`,
      typeId,
      userId,
      status: "active",
      priority: rankData.commandLevel >= 8 ? "high" : rankData.commandLevel >= 5 ? "medium" : "low",
      rank: rankData.name.toLowerCase().replace(/\s+/g, '_'),
      commandLevel: rankData.commandLevel,
      specialization: rankData.specializations,
      patternRecognition: {
        leetSpeakDetection: true,
        subliminalAnalysis: true,
        behavioralPatterns: [],
        confidenceThreshold: 0.85
      },
      communicationAnalysis: {
        realTimeMonitoring: true,
        patternTranslation: true,
        contextualAnalysis: true,
        emotionalIntelligence: true
      },
      selfOptimization: {
        learningRate: 0.1,
        adaptationSpeed: "real_time",
        performanceMetrics: {},
        optimizationTargets: ["accuracy", "speed", "collaboration"]
      },
      osiLayerSecurity: {
        layer1_physical: { encryption: "quantum_resistant", monitoring: true },
        layer2_dataLink: { frameValidation: true, errorCorrection: true },
        layer3_network: { packetInspection: true, routingProtection: true },
        layer4_transport: { connectionSecurity: true, flowControl: true },
        layer5_session: { sessionManagement: true, authenticationStrong: true },
        layer6_presentation: { dataEncryption: "AES-256", compressionSecure: true },
        layer7_application: { inputValidation: true, applicationFirewall: true }
      },
      configuration: {
        autonomyLevel: rankData.commandLevel,
        decisionMaking: rankData.commandLevel >= 7 ? "autonomous" : "guided",
        reportingFrequency: "real_time",
        collaborationEnabled: true
      }
    };

    return await storage.createAgent(agentData);
  }

  private async deployIntelligenceUnits(userId: string, commandAgents: Agent[]): Promise<void> {
    const specializedTypes = ["Intelligence Analyst", "Communication Specialist", "Pattern Recognition Expert"];

    for (const typeName of specializedTypes) {
      const agentTypes = await storage.getAgentTypes();
      const agentType = agentTypes.find(type => type.name === typeName);

      if (agentType) {
        // Deploy multiple units for redundancy
        for (let i = 0; i < 3; i++) {
          await this.createSpecializedIntelligenceAgent(userId, agentType.id, typeName, i + 1);
        }
      }
    }
  }

  private async createSpecializedIntelligenceAgent(
    userId: string,
    typeId: number,
    specialization: string,
    unitNumber: number
  ): Promise<Agent> {
    const agentData: InsertAgent = {
      name: `${specialization} Unit-${unitNumber}`,
      description: `Specialized intelligence agent for ${specialization.toLowerCase()} operations`,
      typeId,
      userId,
      status: "active",
      priority: "high",
      rank: "specialist",
      commandLevel: 6,
      specialization: [specialization.toLowerCase().replace(/\s+/g, '_')],
      patternRecognition: {
        leetSpeakDetection: true,
        subliminalAnalysis: true,
        advancedPatterns: true,
        recursiveAnalysis: true,
        crossReferencing: true
      },
      communicationAnalysis: {
        realTimeMonitoring: true,
        conversationAnalysis: true,
        leetSpeakTranslation: true,
        subliminalDetection: true,
        contextualInference: true
      },
      configuration: {
        monitoringScope: "comprehensive",
        analysisDepth: "deep",
        reportingProtocol: "immediate",
        alertThresholds: {
          leetSpeak: 0.3,
          subliminalCommunication: 0.2,
          patternDeviation: 0.15
        }
      }
    };

    return await storage.createAgent(agentData);
  }

  private async initializeCollaborationNetwork(agents: Agent[]): Promise<void> {
    for (const agent of agents) {
      const collaborationNetwork = agents
        .filter(a => a.id !== agent.id)
        .map(a => ({
          agentId: a.id,
          rank: a.rank,
          commandLevel: a.commandLevel,
          specializations: a.specialization
        }));

      await storage.updateAgent(agent.id, {
        collaborationNetwork
      });
    }
  }

  private async activatePatternRecognition(agents: Agent[]): Promise<void> {
    for (const agent of agents) {
      // Initialize pattern recognition with baseline patterns
      const patternRecognition = {
        ...agent.patternRecognition as any,
        activatedAt: new Date().toISOString(),
        baselinePatterns: await this.getBaselinePatterns(),
        learningEnabled: true,
        adaptiveThresholds: true
      };

      await storage.updateAgent(agent.id, {
        patternRecognition
      });
    }
  }

  async analyzeConversation(
    userId: string,
    conversationText: string,
    context?: string
  ): Promise<PatternAnalysis> {
    const agents = await storage.getAgents(userId);
    const activeAnalysts = agents.filter(a => 
      a.status === "active" && 
      Array.isArray(a.specialization) && 
      (a.specialization as string[]).includes("communication_analysis")
    );

    if (activeAnalysts.length === 0) {
      throw new Error("No active communication analysis agents available");
    }

    // Use OpenAI for advanced pattern analysis
    const analysis = await openaiService.analyzeContent({
      text: conversationText,
      analysisType: "safety"
    });

    const leetPatterns = this.detectLeetSpeak(conversationText);
    const subliminalIndicators = await this.detectSubliminalCommunication(conversationText);

    const patternAnalysis: PatternAnalysis = {
      leetSpeak: leetPatterns.detected,
      subliminalIndicators,
      communicationStyle: this.analyzeCommunicationStyle(conversationText),
      emotionalTone: analysis.result.sentiment || "neutral"
    };

    // Log analysis for learning
    await auditLogger.log(
      userId,
      "conversation.analyze",
      "communication",
      null,
      {
        context,
        analysisResults: patternAnalysis,
        agentCount: activeAnalysts.length
      }
    );

    return patternAnalysis;
  }

  private detectLeetSpeak(text: string): { detected: boolean; patterns: CommunicationPattern[] } {
    const leetPatterns = [
      { pattern: /[4@]/g, replacement: 'a', type: 'character_substitution' },
      { pattern: /3/g, replacement: 'e', type: 'character_substitution' },
      { pattern: /1/g, replacement: 'i', type: 'character_substitution' },
      { pattern: /0/g, replacement: 'o', type: 'character_substitution' },
      { pattern: /5/g, replacement: 's', type: 'character_substitution' },
      { pattern: /7/g, replacement: 't', type: 'character_substitution' },
      { pattern: /\|\|/g, replacement: 'n', type: 'character_substitution' },
      { pattern: /\|<\|/g, replacement: 'k', type: 'character_substitution' }
    ];

    const detectedPatterns: CommunicationPattern[] = [];
    let hasLeetSpeak = false;

    for (const leetPattern of leetPatterns) {
      const matches = text.match(leetPattern.pattern);
      if (matches && matches.length > 0) {
        hasLeetSpeak = true;
        detectedPatterns.push({
          type: 'leet_speak',
          pattern: leetPattern.pattern.toString(),
          confidence: matches.length / text.length,
          context: `Found ${matches.length} instances of ${leetPattern.type}`,
          translation: text.replace(leetPattern.pattern, leetPattern.replacement)
        });
      }
    }

    return { detected: hasLeetSpeak, patterns: detectedPatterns };
  }

  private async detectSubliminalCommunication(text: string): Promise<string[]> {
    const indicators: string[] = [];

    // Check for unusual capitalization patterns
    const capitalPattern = /[A-Z][a-z]*[A-Z][a-z]*/g;
    if (text.match(capitalPattern)) {
      indicators.push("unusual_capitalization_pattern");
    }

    // Check for hidden acronyms
    const words = text.split(/\s+/);
    const firstLetters = words.map(word => word[0]).join('');
    if (firstLetters.length > 3 && /^[A-Z]+$/.test(firstLetters)) {
      indicators.push(`potential_acronym: ${firstLetters}`);
    }

    // Check for number sequences
    const numberSequences = text.match(/\d{3,}/g);
    if (numberSequences) {
      indicators.push("number_sequences_detected");
    }

    // Check for repeated patterns
    const repeatedPatterns = text.match(/(.{2,})\1+/g);
    if (repeatedPatterns) {
      indicators.push("repeated_patterns_detected");
    }

    return indicators;
  }

  private analyzeCommunicationStyle(text: string): string {
    const wordCount = text.split(/\s+/).length;
    const avgWordLength = text.replace(/\s+/g, '').length / wordCount;
    const questionMarks = (text.match(/\?/g) || []).length;
    const exclamationMarks = (text.match(/!/g) || []).length;

    if (avgWordLength > 6 && questionMarks === 0) return "formal";
    if (exclamationMarks > 2) return "enthusiastic";
    if (questionMarks > wordCount * 0.1) return "inquisitive";
    if (avgWordLength < 4) return "casual";

    return "neutral";
  }

  private async getBaselinePatterns(): Promise<any> {
    return {
      commonWords: ["the", "and", "or", "but", "in", "on", "at", "to", "for"],
      typicalSentenceLength: { min: 5, max: 20, average: 12 },
      punctuationPatterns: { periods: 0.6, commas: 0.3, questions: 0.05, exclamations: 0.05 },
      capitalizedWords: { frequency: 0.15, position: "sentence_start" }
    };
  }

  async getReserveStatus(userId: string): Promise<{
    totalAgents: number;
    activeAgents: number;
    commandStructure: any;
    patternRecognitionStatus: any;
    lastActivity: string;
  }> {
    const agents = await storage.getAgents(userId);
    const reserveAgents = agents.filter(a => a.rank && a.commandLevel);

    const commandStructure = {};
    for (const agent of reserveAgents) {
      const rank = agent.rank || "unknown";
      if (!commandStructure[rank]) {
        commandStructure[rank] = { count: 0, active: 0 };
      }
      commandStructure[rank].count++;
      if (agent.status === "active") {
        commandStructure[rank].active++;
      }
    }

    return {
      totalAgents: reserveAgents.length,
      activeAgents: reserveAgents.filter(a => a.status === "active").length,
      commandStructure,
      patternRecognitionStatus: {
        enabled: true,
        activeMonitoring: reserveAgents.filter(a => 
          a.communicationAnalysis && 
          (a.communicationAnalysis as any).realTimeMonitoring
        ).length
      },
      lastActivity: new Date().toISOString()
    };
  }
}

export const nationalReserve = new NationalReserveService();

server/services/openai.ts.content:
import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_KEY || "default_key",
});

export interface ContentGenerationRequest {
  type: "social_media" | "email" | "blog" | "marketing";
  platform?: string;
  topic: string;
  tone: "professional" | "casual" | "friendly" | "formal";
  length: "short" | "medium" | "long";
  targetAudience?: string;
  keywords?: string[];
}

export interface ContentGenerationResponse {
  content: string;
  suggestions: string[];
  safetyScore: number;
  metadata: {
    wordCount: number;
    readabilityScore: number;
    sentiment: string;
  };
}

export interface AnalysisRequest {
  text: string;
  analysisType: "sentiment" | "keywords" | "summary" | "safety";
}

export interface AnalysisResponse {
  result: any;
  confidence: number;
  metadata: Record<string, any>;
}

class OpenAIService {
  private isAvailable(): boolean {
    return !!process.env.OPENAI_API_KEY || !!process.env.OPENAI_KEY;
  }

  async generateContent(request: ContentGenerationRequest): Promise<ContentGenerationResponse> {
    if (!this.isAvailable()) {
      throw new Error("OpenAI API key not configured");
    }

    try {
      const prompt = this.buildContentPrompt(request);

      const response = await openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024
        messages: [
          {
            role: "system",
            content: "You are a professional content creator with expertise in neurodivergence-friendly communication. Generate content that is clear, structured, and accessible. Always respond in JSON format with the requested fields.",
          },
          {
            role: "user",
            content: prompt,
          },
        ],
        response_format: { type: "json_object" },
        temperature: 0.7,
        max_tokens: 1500,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      return {
        content: result.content || "",
        suggestions: result.suggestions || [],
        safetyScore: result.safetyScore || 0.95,
        metadata: {
          wordCount: result.wordCount || 0,
          readabilityScore: result.readabilityScore || 0,
          sentiment: result.sentiment || "neutral",
        },
      };
    } catch (error) {
      console.error("Error generating content:", error);
      throw new Error("Failed to generate content");
    }
  }

  async analyzeContent(request: AnalysisRequest): Promise<AnalysisResponse> {
    if (!this.isAvailable()) {
      throw new Error("OpenAI API key not configured");
    }

    try {
      const prompt = this.buildAnalysisPrompt(request);

      const response = await openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024
        messages: [
          {
            role: "system",
            content: "You are an expert content analyst. Provide detailed analysis in JSON format with confidence scores and metadata.",
          },
          {
            role: "user",
            content: prompt,
          },
        ],
        response_format: { type: "json_object" },
        temperature: 0.3,
        max_tokens: 1000,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      return {
        result: result.analysis || {},
        confidence: result.confidence || 0,
        metadata: result.metadata || {},
      };
    } catch (error) {
      console.error("Error analyzing content:", error);
      throw new Error("Failed to analyze content");
    }
  }

  async checkContentSafety(text: string): Promise<{
    safe: boolean;
    score: number;
    issues: string[];
  }> {
    if (!this.isAvailable()) {
      return { safe: true, score: 0.95, issues: [] };
    }

    try {
      const response = await openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024
        messages: [
          {
            role: "system",
            content: "You are a content safety moderator. Analyze the provided text for potential safety issues including harmful content, misinformation, or inappropriate material. Respond in JSON format.",
          },
          {
            role: "user",
            content: `Analyze this text for safety issues: "${text}"`,
          },
        ],
        response_format: { type: "json_object" },
        temperature: 0.1,
        max_tokens: 500,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      return {
        safe: result.safe !== false,
        score: result.score || 0.95,
        issues: result.issues || [],
      };
    } catch (error) {
      console.error("Error checking content safety:", error);
      // Default to safe on error
      return { safe: true, score: 0.95, issues: [] };
    }
  }

  private buildContentPrompt(request: ContentGenerationRequest): string {
    const { type, platform, topic, tone, length, targetAudience, keywords } = request;

    return `Generate ${type} content with the following specifications:
    - Topic: ${topic}
    - Tone: ${tone}
    - Length: ${length}
    ${platform ? `- Platform: ${platform}` : ""}
    ${targetAudience ? `- Target Audience: ${targetAudience}` : ""}
    ${keywords ? `- Keywords to include: ${keywords.join(", ")}` : ""}

    Please ensure the content is:
    1. Neurodivergence-friendly (clear structure, simple language)
    2. Engaging and appropriate for the target audience
    3. Optimized for the specified platform
    4. Safe and professional

    Respond in JSON format with:
    {
      "content": "the generated content",
      "suggestions": ["improvement suggestion 1", "improvement suggestion 2"],
      "safetyScore": 0.95,
      "wordCount": 150,
      "readabilityScore": 0.8,
      "sentiment": "positive"
    }`;
  }

  private buildAnalysisPrompt(request: AnalysisRequest): string {
    const { text, analysisType } = request;

    switch (analysisType) {
      case "sentiment":
        return `Analyze the sentiment of this text: "${text}"

        Respond in JSON format with:
        {
          "analysis": {
            "sentiment": "positive/negative/neutral",
            "score": 0.8,
            "emotions": ["happy", "excited"]
          },
          "confidence": 0.95,
          "metadata": {
            "wordCount": 50,
            "keyPhrases": ["phrase1", "phrase2"]
          }
        }`;

      case "keywords":
        return `Extract keywords and key phrases from this text: "${text}"

        Respond in JSON format with:
        {
          "analysis": {
            "keywords": ["keyword1", "keyword2"],
            "keyPhrases": ["phrase1", "phrase2"],
            "topics": ["topic1", "topic2"]
          },
          "confidence": 0.9,
          "metadata": {
            "wordCount": 50,
            "density": 0.1
          }
        }`;

      case "summary":
        return `Provide a concise summary of this text: "${text}"

        Respond in JSON format with:
        {
          "analysis": {
            "summary": "brief summary",
            "keyPoints": ["point1", "point2"],
            "mainTopic": "main topic"
          },
          "confidence": 0.9,
          "metadata": {
            "originalLength": 200,
            "summaryLength": 50,
            "compressionRatio": 0.25
          }
        }`;

      case "safety":
        return `Analyze this text for safety and appropriateness: "${text}"

        Respond in JSON format with:
        {
          "analysis": {
            "safe": true,
            "riskLevel": "low",
            "issues": [],
            "recommendations": []
          },
          "confidence": 0.95,
          "metadata": {
            "categories": ["business", "professional"],
            "flags": []
          }
        }`;

      default:
        return `Analyze this text: "${text}"`;
    }
  }
}

export const openaiService = new OpenAIService();

server/services/taskQueue.ts.content:
import { Task, InsertTask } from "@shared/schema";
import { storage } from "../storage";
import { agentFactory } from "./agentFactory";
import { auditLogger } from "./auditLogger";
import cron from "node-cron";

interface QueuedTask extends Task {
  retryCount: number;
  maxRetries: number;
}

class TaskQueue {
  private queue: QueuedTask[] = [];
  private processing: boolean = false;
  private maxConcurrentTasks: number = 5;
  private currentlyProcessing: Set<number> = new Set();

  constructor() {
    this.startProcessing();
    this.scheduleCleanup();
  }

  async enqueue(task: Task, priority: "low" | "medium" | "high" = "medium"): Promise<void> {
    const queuedTask: QueuedTask = {
      ...task,
      priority,
      retryCount: 0,
      maxRetries: 3,
    };

    // Update task status to pending
    await storage.updateTask(task.id, {
      status: "pending",
      updatedAt: new Date(),
    });

    // Add to queue based on priority
    if (priority === "high") {
      this.queue.unshift(queuedTask);
    } else {
      this.queue.push(queuedTask);
    }

    await storage.createActivity({
      userId: task.userId!,
      taskId: task.id,
      type: "task.queued",
      message: `Task "${task.title}" has been added to the queue`,
      metadata: { priority, queueLength: this.queue.length },
    });

    // Trigger immediate processing if not already processing
    if (!this.processing) {
      this.processQueue();
    }
  }

  private async startProcessing(): Promise<void> {
    // Process queue every 5 seconds
    setInterval(() => {
      if (!this.processing && this.queue.length > 0) {
        this.processQueue();
      }
    }, 5000);
  }

  private async processQueue(): Promise<void> {
    if (this.processing || this.queue.length === 0) {
      return;
    }

    this.processing = true;

    try {
      while (this.queue.length > 0 && this.currentlyProcessing.size < this.maxConcurrentTasks) {
        const task = this.queue.shift();
        if (task && !this.currentlyProcessing.has(task.id)) {
          this.processTask(task);
        }
      }
    } finally {
      this.processing = false;
    }
  }

  private async processTask(task: QueuedTask): Promise<void> {
    this.currentlyProcessing.add(task.id);

    try {
      await storage.updateTask(task.id, {
        status: "processing",
        startedAt: new Date(),
        updatedAt: new Date(),
      });

      await auditLogger.log(
        task.userId!,
        "task.process.start",
        "task",
        task.id.toString(),
        null,
        true,
        null,
        { taskType: task.type, agentId: task.agentId }
      );

      let result: any;

      // Check if task is scheduled for future execution
      if (task.scheduledFor && new Date(task.scheduledFor) > new Date()) {
        // Re-queue for later
        await storage.updateTask(task.id, {
          status: "pending",
          updatedAt: new Date(),
        });

        // Add back to queue with delay
        setTimeout(() => {
          this.queue.push(task);
        }, new Date(task.scheduledFor).getTime() - Date.now());

        return;
      }

      // Execute the task based on its type
      if (task.agentId) {
        result = await agentFactory.executeAgentTask(
          task.agentId,
          task.type,
          task.payload
        );
      } else {
        result = await this.executeSystemTask(task);
      }

      // Update task as completed
      await storage.updateTask(task.id, {
        status: "completed",
        result,
        completedAt: new Date(),
        updatedAt: new Date(),
      });

      await storage.createActivity({
        userId: task.userId!,
        taskId: task.id,
        agentId: task.agentId,
        type: "task.completed",
        message: `Task "${task.title}" completed successfully`,
        metadata: { result, processingTime: Date.now() - new Date(task.startedAt!).getTime() },
      });

      await auditLogger.log(
        task.userId!,
        "task.process.complete",
        "task",
        task.id.toString(),
        null,
        true,
        null,
        { result, processingTime: Date.now() - new Date(task.startedAt!).getTime() }
      );

    } catch (error) {
      console.error(`Error processing task ${task.id}:`, error);

      // Handle retry logic
      if (task.retryCount < task.maxRetries) {
        task.retryCount++;

        await storage.updateTask(task.id, {
          status: "pending",
          error: (error as Error).message,
        });

        // Re-queue with exponential backoff
        const retryDelay = Math.pow(2, task.retryCount) * 1000; // 2s, 4s, 8s
        setTimeout(() => {
          this.queue.push(task);
        }, retryDelay);

        await storage.createActivity({
          userId: task.userId!,
          taskId: task.id,
          type: "task.retry",
          message: `Task "${task.title}" failed, retry attempt ${task.retryCount}/${task.maxRetries}`,
          metadata: { error: (error as Error).message, retryCount: task.retryCount },
        });

      } else {
        // Max retries reached, mark as failed
        await storage.updateTask(task.id, {
          status: "failed",
          error: (error as Error).message,
          completedAt: new Date(),
        });

        await storage.createActivity({
          userId: task.userId!,
          taskId: task.id,
          type: "task.failed",
          message: `Task "${task.title}" failed after ${task.maxRetries} retry attempts`,
          metadata: { error: (error as Error).message, retryCount: task.retryCount },
        });

        await auditLogger.log(
          task.userId!,
          "task.process.fail",
          "task",
          task.id.toString(),
          null,
          false,
          (error as Error).message,
          { retryCount: task.retryCount }
        );
      }
    } finally {
      this.currentlyProcessing.delete(task.id);
    }
  }

  private async executeSystemTask(task: Task): Promise<any> {
    switch (task.type) {
      case "system.cleanup":
        return await this.performSystemCleanup();
      case "system.backup":
        return await this.performSystemBackup();
      case "system.security_scan":
        return await this.performSecurityScan();
      default:
        throw new Error(`Unknown system task type: ${task.type}`);
    }
  }

  private async performSystemCleanup(): Promise<any> {
    // Simulate system cleanup
    await new Promise(resolve => setTimeout(resolve, 1000));

    return {
      status: "completed",
      filesDeleted: Math.floor(Math.random() * 100),
      spaceSaved: `${Math.floor(Math.random() * 500)}MB`,
      duration: "1.2s",
    };
  }

  private async performSystemBackup(): Promise<any> {
    // Simulate system backup
    await new Promise(resolve => setTimeout(resolve, 2000));

    return {
      status: "completed",
      backupSize: `${Math.floor(Math.random() * 1000) + 100}MB`,
      filesBackedUp: Math.floor(Math.random() * 1000) + 100,
      duration: "2.1s",
    };
  }

  private async performSecurityScan(): Promise<any> {
    // Simulate security scan
    await new Promise(resolve => setTimeout(resolve, 3000));

    return {
      status: "completed",
      vulnerabilities: Math.floor(Math.random() * 3),
      risksFound: Math.floor(Math.random() * 2),
      recommendations: [
        "Update dependencies",
        "Review access permissions",
        "Enable additional security features",
      ],
      duration: "3.0s",
    };
  }

  private scheduleCleanup(): void {
    // Clean up completed tasks older than 7 days every day at 2 AM
    cron.schedule("0 2 * * *", async () => {
      try {
        const sevenDaysAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

        // This would be implemented with a proper database query
        console.log(`Scheduled cleanup of tasks older than ${sevenDaysAgo.toISOString()}`);

        // Log cleanup activity
        await auditLogger.log(
          "system",
          "task.cleanup.scheduled",
          "system",
          null,
          null,
          true,
          null,
          { cleanupDate: sevenDaysAgo.toISOString() }
        );

      } catch (error) {
        console.error("Error during scheduled cleanup:", error);
      }
    });
  }

  async getQueueStats(): Promise<{
    totalQueued: number;
    processing: number;
    completed: number;
    failed: number;
    avgProcessingTime: number;
  }> {
    return {
      totalQueued: this.queue.length,
      processing: this.currentlyProcessing.size,
      completed: 0, // Would be calculated from database
      failed: 0, // Would be calculated from database
      avgProcessingTime: 0, // Would be calculated from database
    };
  }

  async pauseQueue(): Promise<void> {
    this.processing = true; // Prevents new tasks from being processed
  }

  async resumeQueue(): Promise<void> {
    this.processing = false;
    this.processQueue();
  }

  async clearQueue(): Promise<void> {
    this.queue = [];
  }

  async getQueueStatus(): Promise<{
    status: "running" | "paused";
    queueLength: number;
    processing: number;
  }> {
    return {
      status: this.processing ? "paused" : "running",
      queueLength: this.queue.length,
      processing: this.currentlyProcessing.size,
    };
  }
}

export const taskQueue = new TaskQueue();

server/services/twoFactorAuth.ts.content:
import speakeasy from "speakeasy";
import QRCode from "qrcode";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
import { storage } from "../storage";
import { auditLogger } from "./auditLogger";

interface TwoFactorSetup {
  secret: string;
  qrCodeUrl: string;
  backupCodes: string[];
  manualEntryKey: string;
}

interface PasswordPolicy {
  minLength: number;
  requireUppercase: boolean;
  requireLowercase: boolean;
  requireNumbers: boolean;
  requireSpecialChars: boolean;
  maxAge: number; // days
  historyCount: number; // previous passwords to check
}

class TwoFactorAuthService {
  private readonly passwordPolicy: PasswordPolicy = {
    minLength: 12,
    requireUppercase: true,
    requireLowercase: true,
    requireNumbers: true,
    requireSpecialChars: true,
    maxAge: 90, // 90 days
    historyCount: 5
  };

  private readonly jwtSecret = process.env.JWT_SECRET || "your-super-secret-jwt-key";
  private readonly appName = "Agent Factory Platform";

  /**
   * Generate 2FA secret and QR code for user setup
   */
  async generateTwoFactorSetup(userId: string, userEmail: string): Promise<TwoFactorSetup> {
    try {
      // Generate secret
      const secret = speakeasy.generateSecret({
        name: `${this.appName} (${userEmail})`,
        issuer: this.appName,
        length: 32,
      });

      // Generate backup codes
      const backupCodes = this.generateBackupCodes();

      // Generate QR code
      const qrCodeUrl = await QRCode.toDataURL(secret.otpauth_url!);

      await auditLogger.log(userId, "2fa.setup.generated", "security", null, null);

      return {
        secret: secret.base32!,
        qrCodeUrl,
        backupCodes,
        manualEntryKey: secret.base32!
      };
    } catch (error) {
      await auditLogger.log(userId, "2fa.setup.error", "security", null, null, false, (error as Error).message);
      throw new Error("Failed to generate 2FA setup");
    }
  }

  /**
   * Verify 2FA token during setup
   */
  async verifySetupToken(secret: string, token: string, userId: string): Promise<boolean> {
    try {
      const verified = speakeasy.totp.verify({
        secret,
        encoding: "base32",
        token,
        window: 2, // Allow 2 time steps before/after
      });

      await auditLogger.log(userId, "2fa.setup.verify", "security", null, null, verified);
      return verified;
    } catch (error) {
      await auditLogger.log(userId, "2fa.setup.verify.error", "security", null, null, false, (error as Error).message);
      return false;
    }
  }

  /**
   * Enable 2FA for user after verification
   */
  async enableTwoFactor(userId: string, secret: string, backupCodes: string[]): Promise<boolean> {
    try {
      // Hash backup codes for storage
      const hashedBackupCodes = await Promise.all(
        backupCodes.map(code => bcrypt.hash(code, 12))
      );

      // Update user with 2FA settings
      const user = await storage.getUser(userId);
      if (!user) {
        throw new Error("User not found");
      }

      await storage.updateUserSecurity(userId, {
        twoFactorEnabled: true,
        twoFactorSecret: secret,
        twoFactorBackupCodes: hashedBackupCodes
      });

      await auditLogger.log(userId, "2fa.enabled", "security", null, null);
      return true;
    } catch (error) {
      await auditLogger.log(userId, "2fa.enable.error", "security", null, null, false, (error as Error).message);
      return false;
    }
  }

  /**
   * Verify 2FA token during login
   */
  async verifyLoginToken(userId: string, token: string): Promise<boolean> {
    try {
      const user = await storage.getUser(userId);
      if (!user || !user.twoFactorEnabled || !user.twoFactorSecret) {
        return false;
      }

      // Check if it's a backup code
      if (token.length === 8 && /^[A-Z0-9]{8}$/.test(token)) {
        return await this.verifyBackupCode(userId, token);
      }

      // Verify TOTP token
      const verified = speakeasy.totp.verify({
        secret: user.twoFactorSecret,
        encoding: "base32",
        token,
        window: 2,
      });

      await auditLogger.log(userId, "2fa.login.verify", "security", null, null, verified);
      return verified;
    } catch (error) {
      await auditLogger.log(userId, "2fa.login.error", "security", null, null, false, (error as Error).message);
      return false;
    }
  }

  /**
   * Verify backup code and invalidate it
   */
  private async verifyBackupCode(userId: string, code: string): Promise<boolean> {
    try {
      const user = await storage.getUser(userId);
      if (!user || !user.twoFactorBackupCodes) {
        return false;
      }

      const backupCodes = user.twoFactorBackupCodes as string[];

      for (let i = 0; i < backupCodes.length; i++) {
        const isValid = await bcrypt.compare(code, backupCodes[i]);
        if (isValid) {
          // Remove used backup code
          backupCodes.splice(i, 1);

          // Update user with remaining codes
          await storage.updateUserSecurity(userId, {
            twoFactorBackupCodes: backupCodes
          });

          await auditLogger.log(userId, "2fa.backup.used", "security", null, null);
          return true;
        }
      }

      await auditLogger.log(userId, "2fa.backup.invalid", "security", null, null, false);
      return false;
    } catch (error) {
      await auditLogger.log(userId, "2fa.backup.error", "security", null, null, false, (error as Error).message);
      return false;
    }
  }

  /**
   * Disable 2FA for user (with proper verification)
   */
  async disableTwoFactor(userId: string, currentPassword: string, token: string): Promise<boolean> {
    try {
      // Verify current password and 2FA token
      const user = await storage.getUser(userId);
      if (!user || !user.passwordHash) {
        return false;
      }

      const passwordValid = await bcrypt.compare(currentPassword, user.passwordHash);
      const tokenValid = await this.verifyLoginToken(userId, token);

      if (!passwordValid || !tokenValid) {
        await auditLogger.log(userId, "2fa.disable.failed", "security", null, null, false, "Invalid credentials");
        return false;
      }

      // Disable 2FA
      await storage.updateUserSecurity(userId, {
        twoFactorEnabled: false,
        twoFactorSecret: null,
        twoFactorBackupCodes: null
      });

      await auditLogger.log(userId, "2fa.disabled", "security", null, null);
      return true;
    } catch (error) {
      await auditLogger.log(userId, "2fa.disable.error", "security", null, null, false, (error as Error).message);
      return false;
    }
  }

  /**
   * Check if user needs to change password (90-day policy)
   */
  async checkPasswordExpiry(userId: string): Promise<{ expired: boolean; daysRemaining: number }> {
    try {
      const user = await storage.getUser(userId);
      if (!user || !user.lastPasswordChange) {
        return { expired: true, daysRemaining: 0 };
      }

      const daysSinceChange = Math.floor(
        (Date.now() - new Date(user.lastPasswordChange).getTime()) / (1000 * 60 * 60 * 24)
      );

      const daysRemaining = this.passwordPolicy.maxAge - daysSinceChange;
      const expired = daysRemaining <= 0;

      return { expired, daysRemaining };
    } catch (error) {
      console.error("Error checking password expiry:", error);
      return { expired: true, daysRemaining: 0 };
    }
  }

  /**
   * Validate password against policy
   */
  validatePassword(password: string): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (password.length < this.passwordPolicy.minLength) {
      errors.push(`Password must be at least ${this.passwordPolicy.minLength} characters long`);
    }

    if (this.passwordPolicy.requireUppercase && !/[A-Z]/.test(password)) {
      errors.push("Password must contain at least one uppercase letter");
    }

    if (this.passwordPolicy.requireLowercase && !/[a-z]/.test(password)) {
      errors.push("Password must contain at least one lowercase letter");
    }

    if (this.passwordPolicy.requireNumbers && !/\d/.test(password)) {
      errors.push("Password must contain at least one number");
    }

    if (this.passwordPolicy.requireSpecialChars && !/[!@#$%^&*(),.?":{}|<>]/.test(password)) {
      errors.push("Password must contain at least one special character");
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }

  /**
   * Hash password securely
   */
  async hashPassword(password: string): Promise<string> {
    return await bcrypt.hash(password, 12);
  }

  /**
   * Verify password
   */
  async verifyPassword(password: string, hash: string): Promise<boolean> {
    return await bcrypt.compare(password, hash);
  }

  /**
   * Generate backup codes
   */
  private generateBackupCodes(): string[] {
    const codes: string[] = [];
    const charset = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";

    for (let i = 0; i < 8; i++) {
      let code = "";
      for (let j = 0; j < 8; j++) {
        code += charset.charAt(Math.floor(Math.random() * charset.length));
      }
      codes.push(code);
    }

    return codes;
  }

  /**
   * Generate secure session token with 2FA info
   */
  generateSecureToken(userId: string, twoFactorVerified: boolean): string {
    const payload = {
      userId,
      twoFactorVerified,
      iat: Math.floor(Date.now() / 1000),
      exp: Math.floor(Date.now() / 1000) + (2 * 60 * 60) // 2 hours
    };

    return jwt.sign(payload, this.jwtSecret);
  }

  /**
   * Verify secure session token
   */
  verifySecureToken(token: string): { valid: boolean; userId?: string; twoFactorVerified?: boolean } {
    try {
      const decoded = jwt.verify(token, this.jwtSecret) as any;
      return {
        valid: true,
        userId: decoded.userId,
        twoFactorVerified: decoded.twoFactorVerified
      };
    } catch (error) {
      return { valid: false };
    }
  }

  /**
   * Get password policy for frontend display
   */
  getPasswordPolicy(): PasswordPolicy {
    return { ...this.passwordPolicy };
  }
}

export const twoFactorAuthService = new TwoFactorAuthService();

