import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { AgentType } from "@/types/agent";
import { X } from "lucide-react";

const createAgentSchema = z.object({
  name: z.string().min(1, "Agent name is required"),
  description: z.string().optional(),
  typeId: z.coerce.number().min(1, "Please select an agent type"),
  priority: z.enum(["low", "medium", "high"]),
  approvalRequired: z.enum(["always", "high-risk", "never"]),
  encryptionEnabled: z.boolean().default(true),
  rateLimitingEnabled: z.boolean().default(true),
  auditLoggingEnabled: z.boolean().default(true),
});

type CreateAgentFormData = z.infer<typeof createAgentSchema>;

interface CreateAgentModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function CreateAgentModal({ isOpen, onClose }: CreateAgentModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: agentTypes, isLoading: agentTypesLoading } = useQuery<AgentType[]>({
    queryKey: ["/api/agent-types"],
    enabled: isOpen,
  });

  const form = useForm<CreateAgentFormData>({
    resolver: zodResolver(createAgentSchema),
    defaultValues: {
      name: "",
      description: "",
      priority: "medium",
      approvalRequired: "always",
      encryptionEnabled: true,
      rateLimitingEnabled: true,
      auditLoggingEnabled: true,
    },
  });

  const createAgentMutation = useMutation({
    mutationFn: async (data: CreateAgentFormData) => {
      const agentData = {
        name: data.name,
        description: data.description,
        typeId: data.typeId,
        priority: data.priority,
        securityConfig: {
          approvalRequired: data.approvalRequired === "always",
          encryption: data.encryptionEnabled,
          rateLimiting: data.rateLimitingEnabled,
          auditLogging: data.auditLoggingEnabled,
        },
      };

      await apiRequest("POST", "/api/agents", agentData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Agent created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/agents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      form.reset();
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Error",
        description: error.message || "Failed to create agent",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CreateAgentFormData) => {
    createAgentMutation.mutate(data);
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            Create New Agent
            <Button variant="ghost" size="sm" onClick={handleClose}>
              <X className="w-4 h-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="typeId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Agent Type</FormLabel>
                  <Select 
                    onValueChange={field.onChange} 
                    value={field.value?.toString()}
                    disabled={agentTypesLoading}
                  >
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select an agent type" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {agentTypes?.map((type) => (
                        <SelectItem key={type.id} value={type.id.toString()}>
                          {type.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Agent Name</FormLabel>
                  <FormControl>
                    <Input {...field} placeholder="e.g., LinkedIn Content Creator" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea 
                      {...field} 
                      placeholder="Describe what this agent will do..."
                      className="h-24"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="priority"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Priority Level</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="low">Low</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="approvalRequired"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Approval Required</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="always">Always</SelectItem>
                        <SelectItem value="high-risk">High-risk actions only</SelectItem>
                        <SelectItem value="never">Never</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4">
              <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                Security Configuration
              </h4>
              <div className="space-y-3">
                <FormField
                  control={form.control}
                  name="encryptionEnabled"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-center space-x-3 space-y-0">
                      <FormControl>
                        <Checkbox
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                      <FormLabel className="text-sm font-normal">
                        Enable encryption for sensitive data
                      </FormLabel>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="rateLimitingEnabled"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-center space-x-3 space-y-0">
                      <FormControl>
                        <Checkbox
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                      <FormLabel className="text-sm font-normal">
                        Apply rate limiting
                      </FormLabel>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="auditLoggingEnabled"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-center space-x-3 space-y-0">
                      <FormControl>
                        <Checkbox
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                      <FormLabel className="text-sm font-normal">
                        Enable audit logging
                      </FormLabel>
                    </FormItem>
                  )}
                />
              </div>
            </div>

            <div className="flex justify-end space-x-3 pt-4">
              <Button type="button" variant="outline" onClick={handleClose}>
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={createAgentMutation.isPending}
                className="bg-primary hover:bg-blue-700"
              >
                {createAgentMutation.isPending ? "Creating..." : "Create Agent"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/AgentFactory/Multi.content:
import { useState, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  MessageCircle,
  Image as ImageIcon,
  Mic,
  Upload,
  Send,
  Brain,
  Eye,
  Volume2,
  FileText,
  CheckCircle,
  AlertCircle,
  Loader2,
  Trash2,
  Zap
} from "lucide-react";
import { Agent } from "@/types/agent";

interface MultimodalInterfaceProps {
  agent: Agent;
  onClose: () => void;
}

interface MultimodalResponse {
  id: string;
  agentId: number;
  agentName: string;
  content: string;
  usage?: {
    prompt_tokens?: number;
    completion_tokens?: number;
    total_tokens?: number;
  };
  modalities?: string[];
  processingTime?: number;
  cost?: number;
  metadata?: Record<string, any>;
  state?: string;
}

interface AgentCapabilities {
  text: boolean;
  image: boolean;
  audio: boolean;
  streaming: boolean;
  functions: boolean;
  memory: boolean;
  multimodal_reasoning: boolean;
}

export default function MultimodalInterface({ agent, onClose }: MultimodalInterfaceProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const fileInputRef = useRef<HTMLInputElement>(null);

  const [textInput, setTextInput] = useState("");
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [activeTab, setActiveTab] = useState("chat");
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [conversationHistory, setConversationHistory] = useState<MultimodalResponse[]>([]);

  // Get agent capabilities
  const { data: capabilities, isLoading: capabilitiesLoading } = useQuery<AgentCapabilities>({
    queryKey: [`/api/multimodal/agents/${agent.id}/capabilities`],
  });

  // Chat mutation
  const chatMutation = useMutation({
    mutationFn: async (data: { message: string; sessionId?: string }) => {
      const response = await fetch(`/api/multimodal/agents/${agent.id}/chat`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`Chat failed: ${response.statusText}`);
      }

      return response.json() as Promise<MultimodalResponse>;
    },
    onSuccess: (response) => {
      setConversationHistory(prev => [...prev, response]);
      if (response.metadata?.sessionId && !sessionId) {
        setSessionId(response.metadata.sessionId);
      }
      setTextInput("");
      toast({
        title: "Message sent",
        description: `Response in ${response.processingTime}ms`,
      });
    },
    onError: (error) => {
      toast({
        title: "Chat error",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  // Image processing mutation
  const imageMutation = useMutation({
    mutationFn: async (data: { file: File; prompt: string; sessionId?: string }) => {
      const formData = new FormData();
      formData.append("image", data.file);
      formData.append("prompt", data.prompt);
      if (data.sessionId) {
        formData.append("sessionId", data.sessionId);
      }

      const response = await fetch(`/api/multimodal/agents/${agent.id}/image`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Image processing failed: ${response.statusText}`);
      }

      return response.json() as Promise<MultimodalResponse>;
    },
    onSuccess: (response) => {
      setConversationHistory(prev => [...prev, response]);
      if (response.metadata?.sessionId && !sessionId) {
        setSessionId(response.metadata.sessionId);
      }
      setSelectedFile(null);
      setTextInput("");
      toast({
        title: "Image processed",
        description: `Analysis completed in ${response.processingTime}ms`,
      });
    },
    onError: (error) => {
      toast({
        title: "Image processing error",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  // Audio processing mutation
  const audioMutation = useMutation({
    mutationFn: async (data: { file: File; task: string; sessionId?: string }) => {
      const formData = new FormData();
      formData.append("audio", data.file);
      formData.append("task", data.task);
      if (data.sessionId) {
        formData.append("sessionId", data.sessionId);
      }

      const response = await fetch(`/api/multimodal/agents/${agent.id}/audio`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Audio processing failed: ${response.statusText}`);
      }

      return response.json() as Promise<MultimodalResponse>;
    },
    onSuccess: (response) => {
      setConversationHistory(prev => [...prev, response]);
      if (response.metadata?.sessionId && !sessionId) {
        setSessionId(response.metadata.sessionId);
      }
      setSelectedFile(null);
      toast({
        title: "Audio processed",
        description: `Transcription completed in ${response.processingTime}ms`,
      });
    },
    onError: (error) => {
      toast({
        title: "Audio processing error",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  // Multimodal processing mutation
  const multimodalMutation = useMutation({
    mutationFn: async (data: { text?: string; imageFile?: File; audioFile?: File; sessionId?: string }) => {
      const formData = new FormData();
      if (data.text) formData.append("text", data.text);
      if (data.imageFile) formData.append("image", data.imageFile);
      if (data.audioFile) formData.append("audio", data.audioFile);
      if (data.sessionId) formData.append("sessionId", data.sessionId);

      const response = await fetch(`/api/multimodal/agents/${agent.id}/multimodal`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Multimodal processing failed: ${response.statusText}`);
      }

      return response.json() as Promise<MultimodalResponse>;
    },
    onSuccess: (response) => {
      setConversationHistory(prev => [...prev, response]);
      if (response.metadata?.sessionId && !sessionId) {
        setSessionId(response.metadata.sessionId);
      }
      setSelectedFile(null);
      setTextInput("");
      toast({
        title: "Multimodal analysis complete",
        description: `Processed ${response.modalities?.join(", ")} in ${response.processingTime}ms`,
      });
    },
    onError: (error) => {
      toast({
        title: "Multimodal processing error",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  // Clear agent memory
  const clearMemoryMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/multimodal/agents/${agent.id}/memory/clear`, {
        method: "POST",
      });

      if (!response.ok) {
        throw new Error(`Failed to clear memory: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: () => {
      setConversationHistory([]);
      setSessionId(null);
      toast({
        title: "Memory cleared",
        description: "Agent conversation history has been reset",
      });
    },
    onError: (error) => {
      toast({
        title: "Clear memory error",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleSendMessage = () => {
    if (!textInput.trim()) return;

    chatMutation.mutate({
      message: textInput,
      sessionId: sessionId || undefined,
    });
  };

  const handleProcessImage = () => {
    if (!selectedFile) return;

    imageMutation.mutate({
      file: selectedFile,
      prompt: textInput || "What's in this image?",
      sessionId: sessionId || undefined,
    });
  };

  const handleProcessAudio = () => {
    if (!selectedFile) return;

    audioMutation.mutate({
      file: selectedFile,
      task: "transcribe",
      sessionId: sessionId || undefined,
    });
  };

  const handleMultimodalProcess = () => {
    if (!textInput.trim() && !selectedFile) return;

    const isImage = selectedFile?.type.startsWith("image/");
    const isAudio = selectedFile?.type.startsWith("audio/");

    multimodalMutation.mutate({
      text: textInput || undefined,
      imageFile: isImage && selectedFile ? selectedFile : undefined,
      audioFile: isAudio && selectedFile ? selectedFile : undefined,
      sessionId: sessionId || undefined,
    });
  };

  const formatCost = (cost?: number) => {
    if (!cost) return "$0.000";
    return `$${(cost / 1000000).toFixed(6)}`; // Convert from micro-cents
  };

  const getModalityIcon = (modality: string) => {
    switch (modality) {
      case "text": return <MessageCircle className="w-4 h-4" />;
      case "image": return <Eye className="w-4 h-4" />;
      case "audio": return <Volume2 className="w-4 h-4" />;
      default: return <Brain className="w-4 h-4" />;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white dark:bg-gray-900 rounded-lg shadow-xl w-full max-w-6xl h-5/6 max-h-screen overflow-hidden">
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center space-x-4">
              <Brain className="w-8 h-8 text-blue-600" />
              <div>
                <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                  Multimodal Agent: {agent.name}
                </h2>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  {agent.description}
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              {sessionId && (
                <Badge variant="secondary" className="text-xs">
                  Session: {sessionId.slice(-8)}
                </Badge>
              )}
              <Button
                variant="outline"
                size="sm"
                onClick={() => clearMemoryMutation.mutate()}
                disabled={clearMemoryMutation.isPending}
              >
                <Trash2 className="w-4 h-4 mr-2" />
                Clear Memory
              </Button>
              <Button variant="outline" size="sm" onClick={onClose}>
                Close
              </Button>
            </div>
          </div>

          {/* Capabilities */}
          {!capabilitiesLoading && capabilities && (
            <div className="p-4 bg-gray-50 dark:bg-gray-800 border-b">
              <div className="flex items-center space-x-4 text-sm">
                <span className="font-medium">Capabilities:</span>
                {capabilities.text && (
                  <Badge variant="outline" className="text-green-600">
                    <MessageCircle className="w-3 h-3 mr-1" />
                    Text
                  </Badge>
                )}
                {capabilities.image && (
                  <Badge variant="outline" className="text-blue-600">
                    <Eye className="w-3 h-3 mr-1" />
                    Vision
                  </Badge>
                )}
                {capabilities.audio && (
                  <Badge variant="outline" className="text-purple-600">
                    <Volume2 className="w-3 h-3 mr-1" />
                    Audio
                  </Badge>
                )}
                {capabilities.functions && (
                  <Badge variant="outline" className="text-orange-600">
                    <Zap className="w-3 h-3 mr-1" />
                    Functions
                  </Badge>
                )}
                {capabilities.memory && (
                  <Badge variant="outline" className="text-indigo-600">
                    <Brain className="w-3 h-3 mr-1" />
                    Memory
                  </Badge>
                )}
              </div>
            </div>
          )}

          <div className="flex-1 flex overflow-hidden">
            {/* Conversation Area */}
            <div className="flex-1 flex flex-col">
              <div className="flex-1 overflow-auto p-6 space-y-4">
                {conversationHistory.length === 0 ? (
                  <div className="text-center text-gray-500 dark:text-gray-400 mt-20">
                    <Brain className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <h3 className="text-lg font-medium mb-2">Ready for Multimodal Interaction</h3>
                    <p>Send text, upload images or audio files to start conversing with the agent</p>
                  </div>
                ) : (
                  conversationHistory.map((response, index) => (
                    <Card key={index} className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950">
                      <CardHeader className="pb-3">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-2">
                            <Badge variant="outline">Response #{index + 1}</Badge>
                            <div className="flex space-x-1">
                              {response.modalities?.map((modality, i) => (
                                <div key={i} className="flex items-center">
                                  {getModalityIcon(modality)}
                                </div>
                              ))}
                            </div>
                          </div>
                          <div className="flex items-center space-x-4 text-xs text-gray-500">
                            <span>{response.processingTime}ms</span>
                            <span>{formatCost(response.cost)}</span>
                            {response.usage && (
                              <span>{response.usage.total_tokens} tokens</span>
                            )}
                          </div>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <div className="prose dark:prose-invert max-w-none">
                          <pre className="whitespace-pre-wrap font-sans">
                            {response.content}
                          </pre>
                        </div>
                      </CardContent>
                    </Card>
                  ))
                )}
              </div>

              {/* Input Area */}
              <div className="border-t border-gray-200 dark:border-gray-700 p-6 bg-gray-50 dark:bg-gray-800">
                <Tabs value={activeTab} onValueChange={setActiveTab}>
                  <TabsList className="grid w-full grid-cols-4">
                    <TabsTrigger value="chat" className="flex items-center">
                      <MessageCircle className="w-4 h-4 mr-2" />
                      Chat
                    </TabsTrigger>
                    <TabsTrigger value="image" disabled={!capabilities?.image}>
                      <ImageIcon className="w-4 h-4 mr-2" />
                      Image
                    </TabsTrigger>
                    <TabsTrigger value="audio" disabled={!capabilities?.audio}>
                      <Mic className="w-4 h-4 mr-2" />
                      Audio
                    </TabsTrigger>
                    <TabsTrigger value="multimodal">
                      <Brain className="w-4 h-4 mr-2" />
                      Combined
                    </TabsTrigger>
                  </TabsList>

                  <TabsContent value="chat" className="space-y-4">
                    <div className="flex space-x-2">
                      <Textarea
                        placeholder="Type your message..."
                        value={textInput}
                        onChange={(e) => setTextInput(e.target.value)}
                        onKeyDown={(e) => {
                          if (e.key === "Enter" && !e.shiftKey) {
                            e.preventDefault();
                            handleSendMessage();
                          }
                        }}
                        className="flex-1"
                        rows={3}
                      />
                      <Button
                        onClick={handleSendMessage}
                        disabled={!textInput.trim() || chatMutation.isPending}
                        size="lg"
                      >
                        {chatMutation.isPending ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <Send className="w-4 h-4" />
                        )}
                      </Button>
                    </div>
                  </TabsContent>

                  <TabsContent value="image" className="space-y-4">
                    <div className="flex space-x-2">
                      <div className="flex-1 space-y-2">
                        <Input
                          type="file"
                          accept="image/*"
                          onChange={handleFileSelect}
                          ref={fileInputRef}
                        />
                        <Textarea
                          placeholder="What would you like me to analyze about this image?"
                          value={textInput}
                          onChange={(e) => setTextInput(e.target.value)}
                          rows={2}
                        />
                      </div>
                      <Button
                        onClick={handleProcessImage}
                        disabled={!selectedFile || imageMutation.isPending}
                        size="lg"
                      >
                        {imageMutation.isPending ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <Eye className="w-4 h-4" />
                        )}
                      </Button>
                    </div>
                    {selectedFile && selectedFile.type.startsWith("image/") && (
                      <div className="text-sm text-gray-600">
                        Selected: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                      </div>
                    )}
                  </TabsContent>

                  <TabsContent value="audio" className="space-y-4">
                    <div className="flex space-x-2">
                      <div className="flex-1 space-y-2">
                        <Input
                          type="file"
                          accept="audio/*"
                          onChange={handleFileSelect}
                          ref={fileInputRef}
                        />
                      </div>
                      <Button
                        onClick={handleProcessAudio}
                        disabled={!selectedFile || audioMutation.isPending}
                        size="lg"
                      >
                        {audioMutation.isPending ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <Volume2 className="w-4 h-4" />
                        )}
                      </Button>
                    </div>
                    {selectedFile && selectedFile.type.startsWith("audio/") && (
                      <div className="text-sm text-gray-600">
                        Selected: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                      </div>
                    )}
                  </TabsContent>

                  <TabsContent value="multimodal" className="space-y-4">
                    <div className="flex space-x-2">
                      <div className="flex-1 space-y-2">
                        <Input
                          type="file"
                          accept="image/*,audio/*"
                          onChange={handleFileSelect}
                          ref={fileInputRef}
                        />
                        <Textarea
                          placeholder="Describe what you want me to analyze..."
                          value={textInput}
                          onChange={(e) => setTextInput(e.target.value)}
                          rows={3}
                        />
                      </div>
                      <Button
                        onClick={handleMultimodalProcess}
                        disabled={(!textInput.trim() && !selectedFile) || multimodalMutation.isPending}
                        size="lg"
                      >
                        {multimodalMutation.isPending ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <Brain className="w-4 h-4" />
                        )}
                      </Button>
                    </div>
                    {selectedFile && (
                      <div className="text-sm text-gray-600">
                        Selected: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                      </div>
                    )}
                  </TabsContent>
                </Tabs>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/Approvals/Approval.content:
import { Approval } from "@/types/task";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { formatDistanceToNow } from "date-fns";
import { Check, X, Clock, AlertCircle, Eye } from "lucide-react";

interface ApprovalCardProps {
  approval: Approval;
  onApprove?: (approvalId: number) => void;
  onReject?: (approvalId: number) => void;
  onView?: (approval: Approval) => void;
}

function getStatusConfig(status: string) {
  switch (status) {
    case "pending":
      return {
        icon: Clock,
        color: "bg-warning/10 text-warning",
        label: "Pending Review",
      };
    case "approved":
      return {
        icon: Check,
        color: "bg-success/10 text-success",
        label: "Approved",
      };
    case "rejected":
      return {
        icon: X,
        color: "bg-error/10 text-error",
        label: "Rejected",
      };
    default:
      return {
        icon: AlertCircle,
        color: "bg-gray-100 text-gray-600",
        label: status,
      };
  }
}

function getApprovalTypeIcon(type: string) {
  // Return appropriate icons based on approval type
  return AlertCircle; // Default icon
}

export default function ApprovalCard({ 
  approval, 
  onApprove, 
  onReject, 
  onView 
}: ApprovalCardProps) {
  const statusConfig = getStatusConfig(approval.status);
  const StatusIcon = statusConfig.icon;
  const TypeIcon = getApprovalTypeIcon(approval.type);

  const isPending = approval.status === "pending";
  const isCompleted = approval.status === "approved" || approval.status === "rejected";

  return (
    <Card>
      <CardContent className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center space-x-3 mb-2">
              <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center">
                <TypeIcon className="w-4 h-4 text-primary" />
              </div>
              <div>
                <h3 className="font-semibold text-gray-900 dark:text-white">
                  {approval.title}
                </h3>
                <Badge className={cn("text-xs", statusConfig.color)}>
                  <StatusIcon className="w-3 h-3 mr-1" />
                  {statusConfig.label}
                </Badge>
              </div>
            </div>

            {approval.description && (
              <p className="text-sm text-gray-600 dark:text-gray-300 mb-3">
                {approval.description}
              </p>
            )}

            {approval.suggestedResponse && (
              <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-3 mb-3">
                <p className="text-sm font-medium text-blue-900 dark:text-blue-100 mb-1">
                  Suggested Response:
                </p>
                <p className="text-sm text-blue-800 dark:text-blue-200">
                  {approval.suggestedResponse}
                </p>
              </div>
            )}

            <div className="flex items-center space-x-4 text-xs text-gray-500 dark:text-gray-400">
              <span>Type: {approval.type}</span>
              {approval.createdAt && (
                <span>
                  Requested {formatDistanceToNow(new Date(approval.createdAt), { addSuffix: true })}
                </span>
              )}
              {isCompleted && approval.reviewedAt && (
                <span>
                  Reviewed {formatDistanceToNow(new Date(approval.reviewedAt), { addSuffix: true })}
                </span>
              )}
            </div>
          </div>

          <div className="flex items-center space-x-2 ml-4">
            {onView && (
              <Button
                size="sm"
                variant="outline"
                onClick={() => onView(approval)}
              >
                <Eye className="w-4 h-4 mr-1" />
                View
              </Button>
            )}

            {isPending && onApprove && (
              <Button
                size="sm"
                className="bg-success hover:bg-success/90 text-white"
                onClick={() => onApprove(approval.id)}
              >
                <Check className="w-4 h-4 mr-1" />
                Approve
              </Button>
            )}

            {isPending && onReject && (
              <Button
                size="sm"
                variant="destructive"
                onClick={() => onReject(approval.id)}
              >
                <X className="w-4 h-4 mr-1" />
                Reject
              </Button>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/Dashboard/Activity.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Activity } from "@/types/task";
import { CheckCircle, Clock, Cog, Mail } from "lucide-react";
import { cn } from "@/lib/utils";
import { formatDistanceToNow } from "date-fns";

function getActivityIcon(type: string) {
  switch (type) {
    case "agent.created":
    case "agent.updated":
    case "agent.initialized":
      return { icon: Cog, color: "bg-primary/10 text-primary" };
    case "task.completed":
    case "approval.approved":
      return { icon: CheckCircle, color: "bg-success/10 text-success" };
    case "task.pending":
    case "approval.pending":
      return { icon: Clock, color: "bg-warning/10 text-warning" };
    case "email.sent":
      return { icon: Mail, color: "bg-purple-100 text-purple-600" };
    default:
      return { icon: Cog, color: "bg-gray-100 text-gray-600" };
  }
}

export default function ActivityFeed() {
  const { data: activities, isLoading, error } = useQuery<Activity[]>({
    queryKey: ["/api/dashboard/activities"],
    refetchInterval: 120000, // Refresh every 2 minutes to reduce API load
    refetchOnWindowFocus: false, // Prevent refetch on window focus
    refetchOnMount: true,
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Recent Activity</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="flex items-start space-x-3">
                <Skeleton className="w-8 h-8 rounded-full" />
                <div className="flex-1 space-y-2">
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-3 w-1/4" />
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Recent Activity</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-error text-center py-4">Failed to load activity feed</p>
        </CardContent>
      </Card>
    );
  }

  if (!activities || activities.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Recent Activity</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-gray-500 dark:text-gray-400 text-center py-4">
            No recent activity to display
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle>Recent Activity</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {activities.map((activity) => {
            const { icon: Icon, color } = getActivityIcon(activity.type);

            return (
              <div key={activity.id} className="flex items-start space-x-3">
                <div className={cn(
                  "w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0",
                  color
                )}>
                  <Icon className="w-4 h-4" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm text-gray-900 dark:text-white">
                    {activity.message}
                  </p>
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    {activity.createdAt 
                      ? formatDistanceToNow(new Date(activity.createdAt), { addSuffix: true })
                      : "Just now"
                    }
                  </p>
                </div>
              </div>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/Dashboard/QuickAct.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { 
  Sparkles, 
  Send, 
  PieChart, 
  Shield 
} from "lucide-react";

interface QuickAction {
  name: string;
  icon: typeof Sparkles;
  color: string;
  action: string;
  description: string;
}

export default function QuickActions() {
  const { toast } = useToast();

  const quickActions: QuickAction[] = [
    {
      name: "Generate Content",
      icon: Sparkles,
      color: "bg-purple-100 text-purple-600",
      action: "content-generation",
      description: "Create social media content with AI",
    },
    {
      name: "Launch Campaign",
      icon: Send,
      color: "bg-blue-100 text-blue-600",
      action: "email-campaign",
      description: "Start a new email marketing campaign",
    },
    {
      name: "View Analytics",
      icon: PieChart,
      color: "bg-green-100 text-green-600",
      action: "analytics-report",
      description: "Generate performance reports",
    },
    {
      name: "System Check",
      icon: Shield,
      color: "bg-secure/10 text-secure",
      action: "system-check",
      description: "Run security diagnostics",
    },
  ];

  const handleQuickAction = async (action: string, name: string) => {
    try {
      switch (action) {
        case "content-generation":
          window.location.href = "/agents";
          break;
        case "email-campaign":
          window.location.href = "/agents";
          break;
        case "analytics-report":
          window.location.href = "/analytics";
          break;
        case "system-check":
          window.location.href = "/security";
          break;
        default:
          toast({
            title: "Quick Action",
            description: `${name} workflow activated`,
          });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: `Failed to activate ${name}`,
        variant: "destructive",
      });
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Quick Actions</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {quickActions.map((action) => (
            <Button
              key={action.action}
              variant="outline"
              className="flex flex-col items-center p-4 h-auto border hover:border-primary hover:bg-primary/5 transition-colors"
              onClick={() => handleQuickAction(action.action, action.name)}
            >
              <div className={`w-12 h-12 rounded-lg flex items-center justify-center mb-3 ${action.color}`}>
                <action.icon className="w-6 h-6" />
              </div>
