client/src/components/Security/PasswordC.content:
import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogHeader, 
  DialogTitle 
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Shield, 
  Eye, 
  EyeOff, 
  CheckCircle, 
  X, 
  AlertTriangle,
  Key
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface PasswordPolicy {
  minLength: number;
  requireUppercase: boolean;
  requireLowercase: boolean;
  requireNumbers: boolean;
  requireSpecialChars: boolean;
  maxAge: number;
  historyCount: number;
}

interface PasswordChangeModalProps {
  isOpen: boolean;
  onClose: () => void;
  isForced?: boolean; // For mandatory password changes
  userHas2FA?: boolean;
}

export default function PasswordChangeModal({ 
  isOpen, 
  onClose, 
  isForced = false,
  userHas2FA = false 
}: PasswordChangeModalProps) {
  const { toast } = useToast();
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [twoFactorCode, setTwoFactorCode] = useState("");
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Fetch password policy
  const { data: passwordPolicy } = useQuery<PasswordPolicy>({
    queryKey: ["/api/auth/password-policy"],
  });

  // Password change mutation
  const changePasswordMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/auth/change-password", {
        currentPassword,
        newPassword,
        twoFactorToken: userHas2FA ? twoFactorCode : undefined
      });
    },
    onSuccess: () => {
      toast({
        title: "Password Changed",
        description: "Your password has been updated successfully",
      });
      handleClose();
    },
    onError: (error: any) => {
      toast({
        title: "Password Change Failed",
        description: error.message || "Failed to change password",
        variant: "destructive",
      });
    },
  });

  const handleClose = () => {
    if (isForced) return; // Can't close if forced

    setCurrentPassword("");
    setNewPassword("");
    setConfirmPassword("");
    setTwoFactorCode("");
    onClose();
  };

  const validatePassword = (password: string) => {
    if (!passwordPolicy) return { score: 0, checks: [] };

    const checks = [
      { 
        label: `At least ${passwordPolicy.minLength} characters`, 
        passed: password.length >= passwordPolicy.minLength 
      },
      { 
        label: "Contains uppercase letter", 
        passed: !passwordPolicy.requireUppercase || /[A-Z]/.test(password) 
      },
      { 
        label: "Contains lowercase letter", 
        passed: !passwordPolicy.requireLowercase || /[a-z]/.test(password) 
      },
      { 
        label: "Contains number", 
        passed: !passwordPolicy.requireNumbers || /\d/.test(password) 
      },
      { 
        label: "Contains special character", 
        passed: !passwordPolicy.requireSpecialChars || /[!@#$%^&*(),.?":{}|<>]/.test(password) 
      },
    ];

    const score = (checks.filter(check => check.passed).length / checks.length) * 100;
    return { score, checks };
  };

  const passwordValidation = validatePassword(newPassword);
  const passwordsMatch = newPassword === confirmPassword;
  const allFieldsValid = currentPassword && 
                        newPassword && 
                        confirmPassword && 
                        passwordsMatch && 
                        passwordValidation.score === 100 &&
                        (!userHas2FA || twoFactorCode.length === 6);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!allFieldsValid) {
      toast({
        title: "Validation Error",
        description: "Please ensure all fields are valid before submitting",
        variant: "destructive",
      });
      return;
    }

    changePasswordMutation.mutate();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <div className="flex items-center space-x-2">
            <Shield className="w-5 h-5 text-blue-600" />
            <DialogTitle>
              {isForced ? "Password Change Required" : "Change Password"}
            </DialogTitle>
          </div>
          <DialogDescription>
            {isForced 
              ? "Your password has expired. Please choose a new secure password to continue."
              : "Update your account password to maintain security."
            }
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {isForced && (
            <Alert variant="destructive">
              <AlertTriangle className="w-4 h-4" />
              <AlertDescription>
                Your password has been expired for security reasons. 
                You must change it now to continue using the platform.
              </AlertDescription>
            </Alert>
          )}

          {/* Current Password */}
          <div className="space-y-2">
            <Label htmlFor="current-password">Current Password</Label>
            <div className="relative">
              <Input
                id="current-password"
                type={showCurrentPassword ? "text" : "password"}
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                required
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-2 top-1/2 -translate-y-1/2 h-auto p-1"
                onClick={() => setShowCurrentPassword(!showCurrentPassword)}
              >
                {showCurrentPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              </Button>
            </div>
          </div>

          {/* New Password */}
          <div className="space-y-2">
            <Label htmlFor="new-password">New Password</Label>
            <div className="relative">
              <Input
                id="new-password"
                type={showNewPassword ? "text" : "password"}
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-2 top-1/2 -translate-y-1/2 h-auto p-1"
                onClick={() => setShowNewPassword(!showNewPassword)}
              >
                {showNewPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              </Button>
            </div>

            {newPassword && (
              <div className="space-y-2">
                <div className="flex items-center space-x-2">
                  <Progress value={passwordValidation.score} className="flex-1" />
                  <span className="text-sm text-gray-500">
                    {passwordValidation.score.toFixed(0)}%
                  </span>
                </div>
                <div className="space-y-1">
                  {passwordValidation.checks.map((check, index) => (
                    <div key={index} className="flex items-center space-x-2 text-sm">
                      {check.passed ? (
                        <CheckCircle className="w-3 h-3 text-green-500" />
                      ) : (
                        <X className="w-3 h-3 text-red-500" />
                      )}
                      <span className={check.passed ? "text-green-600" : "text-red-600"}>
                        {check.label}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Confirm Password */}
          <div className="space-y-2">
            <Label htmlFor="confirm-password">Confirm New Password</Label>
            <div className="relative">
              <Input
                id="confirm-password"
                type={showConfirmPassword ? "text" : "password"}
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-2 top-1/2 -translate-y-1/2 h-auto p-1"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              >
                {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              </Button>
            </div>
            {confirmPassword && !passwordsMatch && (
              <p className="text-sm text-red-600">Passwords do not match</p>
            )}
          </div>

          {/* Two-Factor Code */}
          {userHas2FA && (
            <div className="space-y-2">
              <Label htmlFor="two-factor-code">
                <div className="flex items-center space-x-2">
                  <Key className="w-4 h-4" />
                  <span>Two-Factor Authentication Code</span>
                </div>
              </Label>
              <Input
                id="two-factor-code"
                type="text"
                placeholder="Enter 6-digit code"
                value={twoFactorCode}
                onChange={(e) => setTwoFactorCode(e.target.value.replace(/\D/g, '').slice(0, 6))}
                maxLength={6}
                className="text-center text-lg tracking-wider"
                required
              />
              <p className="text-sm text-gray-500">
                Enter the code from your authenticator app
              </p>
            </div>
          )}

          <div className="flex space-x-3 pt-4">
            <Button
              type="submit"
              disabled={!allFieldsValid || changePasswordMutation.isPending}
              className="flex-1"
            >
              {changePasswordMutation.isPending ? "Changing..." : "Change Password"}
            </Button>
            {!isForced && (
              <Button type="button" variant="outline" onClick={handleClose}>
                Cancel
              </Button>
            )}
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Security/TwoFactor.content:
import { useState } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Shield, 
  Smartphone, 
  Key, 
  Copy, 
  CheckCircle, 
  AlertTriangle,
  QrCode,
  Download
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface TwoFactorSetup {
  secret: string;
  qrCodeUrl: string;
  backupCodes: string[];
  manualEntryKey: string;
}

interface TwoFactorSetupProps {
  onComplete: () => void;
  onCancel: () => void;
}

export default function TwoFactorSetup({ onComplete, onCancel }: TwoFactorSetupProps) {
  const { toast } = useToast();
  const [verificationCode, setVerificationCode] = useState("");
  const [step, setStep] = useState<"setup" | "verify" | "backup">("setup");
  const [setupData, setSetupData] = useState<TwoFactorSetup | null>(null);

  // Generate 2FA setup
  const setupMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("GET", "/api/auth/2fa/setup") as TwoFactorSetup;
    },
    onSuccess: (data) => {
      setSetupData(data);
      setStep("verify");
      toast({
        title: "2FA Setup Generated",
        description: "Scan the QR code with your authenticator app",
      });
    },
    onError: (error) => {
      toast({
        title: "Setup Error",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  // Verify and enable 2FA
  const verifyMutation = useMutation({
    mutationFn: async () => {
      if (!setupData) throw new Error("Setup data not available");

      return await apiRequest("POST", "/api/auth/2fa/verify-setup", {
        secret: setupData.secret,
        token: verificationCode,
        backupCodes: setupData.backupCodes
      });
    },
    onSuccess: () => {
      setStep("backup");
      toast({
        title: "2FA Enabled",
        description: "Two-factor authentication has been successfully enabled",
      });
    },
    onError: (error) => {
      toast({
        title: "Verification Failed",
        description: (error as Error).message,
        variant: "destructive",
      });
    },
  });

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "Text copied to clipboard",
    });
  };

  const downloadBackupCodes = () => {
    if (!setupData) return;

    const content = `Agent Factory Platform - Backup Codes\n\nGenerated: ${new Date().toLocaleString()}\n\nUse these codes if you lose access to your authenticator app:\n\n${setupData.backupCodes.join('\n')}\n\nIMPORTANT:\n- Each code can only be used once\n- Store these codes in a secure location\n- Do not share these codes with anyone`;

    const blob = new Blob([content], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'agent-factory-backup-codes.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);

    toast({
      title: "Downloaded",
      description: "Backup codes downloaded successfully",
    });
  };

  const handleVerifyCode = () => {
    if (!verificationCode.trim()) {
      toast({
        title: "Verification Required",
        description: "Please enter the verification code from your authenticator app",
        variant: "destructive",
      });
      return;
    }

    verifyMutation.mutate();
  };

  const handleComplete = () => {
    onComplete();
    toast({
      title: "2FA Setup Complete",
      description: "Your account is now protected with two-factor authentication",
    });
  };

  return (
    <div className="w-full p-4">
      <Card className="max-w-2xl mx-auto">
        <CardHeader>
          <div className="flex items-center space-x-2">
            <Shield className="w-6 h-6 text-blue-600" />
            <div>
              <CardTitle>Two-Factor Authentication Setup</CardTitle>
              <CardDescription>
                Add an extra layer of security to your National Reserve account
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {step === "setup" && (
            <div className="space-y-6">
              <Alert>
                <Shield className="w-4 h-4" />
                <AlertDescription>
                  Two-factor authentication (2FA) adds an extra layer of security to your account. 
                  You'll need an authenticator app like Google Authenticator, Authy, or Microsoft Authenticator.
                </AlertDescription>
              </Alert>

              <div className="space-y-4">
                <h3 className="text-lg font-medium">What you'll need:</h3>
                <ul className="space-y-2 text-sm text-gray-600 dark:text-gray-300">
                  <li className="flex items-center space-x-2">
                    <Smartphone className="w-4 h-4" />
                    <span>A smartphone with an authenticator app installed</span>
                  </li>
                  <li className="flex items-center space-x-2">
                    <Key className="w-4 h-4" />
                    <span>Access to scan a QR code or enter a setup key</span>
                  </li>
                  <li className="flex items-center space-x-2">
                    <Download className="w-4 h-4" />
                    <span>A secure place to store backup recovery codes</span>
                  </li>
                </ul>
              </div>

              <div className="flex space-x-3">
                <Button 
                  onClick={() => setupMutation.mutate()}
                  disabled={setupMutation.isPending}
                  className="flex-1"
                >
                  {setupMutation.isPending ? "Generating..." : "Start Setup"}
                </Button>
                <Button variant="outline" onClick={onCancel}>
                  Cancel
                </Button>
              </div>
            </div>
          )}

          {step === "verify" && setupData && (
            <div className="space-y-6">
              <Alert>
                <QrCode className="w-4 h-4" />
                <AlertDescription>
                  Scan the QR code below with your authenticator app, then enter the 6-digit code to verify setup.
                </AlertDescription>
              </Alert>

              <Tabs defaultValue="qr" className="space-y-4">
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="qr">QR Code</TabsTrigger>
                  <TabsTrigger value="manual">Manual Entry</TabsTrigger>
                </TabsList>

                <TabsContent value="qr" className="space-y-4">
                  <div className="flex flex-col items-center space-y-4">
                    <div className="p-4 bg-white rounded-lg border">
                      <img 
                        src={setupData.qrCodeUrl} 
                        alt="2FA QR Code" 
                        className="w-48 h-48"
                      />
                    </div>
                    <p className="text-sm text-gray-500 text-center">
                      Scan this QR code with your authenticator app
                    </p>
                  </div>
                </TabsContent>

                <TabsContent value="manual" className="space-y-4">
                  <div className="space-y-3">
                    <Label>Manual Entry Key:</Label>
                    <div className="flex items-center space-x-2">
                      <Input 
                        value={setupData.manualEntryKey} 
                        readOnly 
                        className="font-mono text-sm"
                      />
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => copyToClipboard(setupData.manualEntryKey)}
                      >
                        <Copy className="w-4 h-4" />
                      </Button>
                    </div>
                    <p className="text-sm text-gray-500">
                      Enter this key manually in your authenticator app if you can't scan the QR code
                    </p>
                  </div>
                </TabsContent>
              </Tabs>

              <div className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="verification-code">Verification Code</Label>
                  <Input
                    id="verification-code"
                    type="text"
                    placeholder="Enter 6-digit code"
                    value={verificationCode}
                    onChange={(e) => setVerificationCode(e.target.value.replace(/\D/g, '').slice(0, 6))}
                    maxLength={6}
                    className="text-center text-lg tracking-wider"
                  />
                  <p className="text-sm text-gray-500">
                    Enter the 6-digit code from your authenticator app
                  </p>
                </div>

                <div className="flex space-x-3">
                  <Button 
                    onClick={handleVerifyCode}
                    disabled={verificationCode.length !== 6 || verifyMutation.isPending}
                    className="flex-1"
                  >
                    {verifyMutation.isPending ? "Verifying..." : "Verify & Enable"}
                  </Button>
                  <Button variant="outline" onClick={onCancel}>
                    Cancel
                  </Button>
                </div>
              </div>
            </div>
          )}

          {step === "backup" && setupData && (
            <div className="space-y-6">
              <Alert>
                <Key className="w-4 h-4" />
                <AlertDescription>
                  <strong>Important:</strong> Save these backup codes in a secure location. 
                  You can use them to access your account if you lose your authenticator device.
                </AlertDescription>
              </Alert>

              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium">Backup Recovery Codes</h3>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={downloadBackupCodes}
                  >
                    <Download className="w-4 h-4 mr-2" />
                    Download
                  </Button>
                </div>

                <div className="grid grid-cols-2 gap-2 p-4 bg-gray-50 dark:bg-gray-800 rounded-lg">
                  {setupData.backupCodes.map((code, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <Badge variant="outline" className="font-mono">
                        {code}
                      </Badge>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => copyToClipboard(code)}
                        className="h-6 w-6 p-0"
                      >
                        <Copy className="w-3 h-3" />
                      </Button>
                    </div>
                  ))}
                </div>

                <Alert variant="destructive">
                  <AlertTriangle className="w-4 h-4" />
                  <AlertDescription>
                    <strong>Security Notice:</strong>
                    <ul className="mt-2 space-y-1 text-sm">
                      <li>â€¢ Each backup code can only be used once</li>
                      <li>â€¢ Store these codes in a secure password manager</li>
                      <li>â€¢ Do not share these codes with anyone</li>
                      <li>â€¢ Generate new codes if you suspect they're compromised</li>
                    </ul>
                  </AlertDescription>
                </Alert>

                <div className="flex items-center justify-center">
                  <Button onClick={handleComplete} className="w-full">
                    <CheckCircle className="w-4 h-4 mr-2" />
                    Complete Setup
                  </Button>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/TaskQueue/TaskCard.content:
import { Task } from "@/types/task";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { formatDistanceToNow } from "date-fns";
import { 
  Clock, 
  Play, 
  CheckCircle, 
  XCircle, 
  AlertCircle,
  MoreHorizontal
} from "lucide-react";

interface TaskCardProps {
  task: Task;
  onStatusChange?: (taskId: number, status: string) => void;
}

function getStatusConfig(status: string) {
  switch (status) {
    case "pending":
      return {
        icon: Clock,
        color: "bg-gray-100 text-gray-600",
        label: "Pending",
      };
    case "processing":
      return {
        icon: Play,
        color: "bg-blue-100 text-blue-600",
        label: "Processing",
      };
    case "completed":
      return {
        icon: CheckCircle,
        color: "bg-success/10 text-success",
        label: "Completed",
      };
    case "failed":
      return {
        icon: XCircle,
        color: "bg-error/10 text-error",
        label: "Failed",
      };
    case "cancelled":
      return {
        icon: AlertCircle,
        color: "bg-warning/10 text-warning",
        label: "Cancelled",
      };
    default:
      return {
        icon: Clock,
        color: "bg-gray-100 text-gray-600",
        label: status,
      };
  }
}

function getPriorityColor(priority: string) {
  switch (priority) {
    case "high":
      return "border-l-error";
    case "medium":
      return "border-l-warning";
    case "low":
      return "border-l-success";
    default:
      return "border-l-gray-300";
  }
}

export default function TaskCard({ task, onStatusChange }: TaskCardProps) {
  const statusConfig = getStatusConfig(task.status);
  const StatusIcon = statusConfig.icon;

  return (
    <Card className={cn("border-l-4", getPriorityColor(task.priority))}>
      <CardContent className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center space-x-3 mb-2">
              <h3 className="font-semibold text-gray-900 dark:text-white">
                {task.title}
              </h3>
              <Badge className={cn("text-xs", statusConfig.color)}>
                <StatusIcon className="w-3 h-3 mr-1" />
                {statusConfig.label}
              </Badge>
              <Badge variant="outline" className="text-xs">
                {task.priority}
              </Badge>
            </div>

            {task.description && (
              <p className="text-sm text-gray-600 dark:text-gray-300 mb-3">
                {task.description}
              </p>
            )}

            <div className="flex items-center space-x-4 text-xs text-gray-500 dark:text-gray-400">
              <span>Type: {task.type}</span>
              {task.createdAt && (
                <span>
                  Created {formatDistanceToNow(new Date(task.createdAt), { addSuffix: true })}
                </span>
              )}
              {task.scheduledFor && new Date(task.scheduledFor) > new Date() && (
                <span>
                  Scheduled for {formatDistanceToNow(new Date(task.scheduledFor), { addSuffix: true })}
                </span>
              )}
            </div>

            {task.error && (
              <div className="mt-3 p-2 bg-error/10 border border-error/20 rounded text-sm text-error">
                Error: {task.error}
              </div>
            )}

            {task.result && task.status === "completed" && (
              <div className="mt-3 p-2 bg-success/10 border border-success/20 rounded text-sm">
                <strong>Result:</strong> {JSON.stringify(task.result, null, 2)}
              </div>
            )}
          </div>

          <div className="flex items-center space-x-2 ml-4">
            {task.status === "pending" && onStatusChange && (
              <Button
                size="sm"
                variant="outline"
                onClick={() => onStatusChange(task.id, "cancelled")}
              >
                Cancel
              </Button>
            )}

            {task.status === "failed" && onStatusChange && (
              <Button
                size="sm"
                variant="outline"
                onClick={() => onStatusChange(task.id, "pending")}
              >
                Retry
              </Button>
            )}

            <Button size="sm" variant="ghost">
              <MoreHorizontal className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

